     1→using System.Text.Json;
     2→using DefaultEcs;
     3→using DefaultEcs.System;
     4→using Microsoft.Xna.Framework;
     5→using Microsoft.Xna.Framework.Content;
     6→using Microsoft.Xna.Framework.Graphics;
     7→using System;
     8→using System.Collections.Generic;
     9→using System.IO;
    10→using System.Linq;
    11→using MonoDreams.Component;
    12→using MonoDreams.Component.Collision;
    13→using MonoDreams.Component.Physics;
    14→using MonoDreams.Component.Draw;
    15→using MonoDreams.Level;
    16→using MonoDreams.Message.Level;
    17→using MonoDreams.State;
    18→using Logger = MonoDreams.State.Logger;
    19→
    20→namespace MonoDreams.System.Level;
    21→
    22→/// <summary>
    23→/// Parses Blender-exported JSON level files and creates entities for meshes.
    24→/// Handles LoadLevelRequest messages where the identifier starts with "Blender_".
    25→/// </summary>
    26→public sealed class BlenderLevelParserSystem : ISystem<GameState>
    27→{
    28→    private const float DEFAULT_LAYER_DEPTH = 0.5f;
    29→
    30→    private readonly World _world;
    31→    private readonly ContentManager _content;
    32→    private readonly MonoDreams.Component.Camera _camera;
    33→    private readonly Dictionary<string, Texture2D> _loadedTextures = new();
    34→    private readonly HashSet<Entity> _blenderEntities = new();
    35→    private readonly Dictionary<string, Action<Entity>> _collectionHandlers = new();
    36→
    37→    public bool IsEnabled { get; set; } = true;
    38→
    39→    public BlenderLevelParserSystem(World world, ContentManager content, MonoDreams.Component.Camera camera)
    40→    {
    41→        _world = world ?? throw new ArgumentNullException(nameof(world));
    42→        _content = content ?? throw new ArgumentNullException(nameof(content));
    43→        _camera = camera ?? throw new ArgumentNullException(nameof(camera));
    44→
    45→        _world.Subscribe<LoadLevelRequest>(OnLoadLevelRequest);
    46→    }
    47→
    48→    /// <summary>
    49→    /// Registers a handler that will be called for entities belonging to the specified Blender collection.
    50→    /// This allows game code to add game-specific components to entities created by the core parser.
    51→    /// </summary>
    52→    public void RegisterCollectionHandler(string collectionName, Action<Entity> handler)
    53→    {
    54→        _collectionHandlers[collectionName] = handler ?? throw new ArgumentNullException(nameof(handler));
    55→    }
    56→
    57→    [Subscribe]
    58→    private void OnLoadLevelRequest(in LoadLevelRequest request)
    59→    {
    60→        if (!IsEnabled) return;
    61→
    62→        // Only handle Blender levels
    63→        if (!request.LevelIdentifier.StartsWith("Blender_"))
    64→            return;
    65→
    66→        Logger.Info($"Loading Blender level: {request.LevelIdentifier}");
    67→
    68→        // Clean up previous entities
    69→        CleanupEntities();
    70→
    71→        try
    72→        {
    73→            // Load JSON file from Content directory
    74→            // The Content.RootDirectory gives us the path to the Content folder
    75→            var jsonPath = Path.Combine(AppDomain.CurrentDomain.BaseDirectory, _content.RootDirectory, "blender_level.json");
    76→            Logger.Debug($"Loading Blender level from: {jsonPath}");
    77→            var jsonContent = File.ReadAllText(jsonPath);
    78→
    79→            var options = new JsonSerializerOptions
    80→            {
    81→                PropertyNameCaseInsensitive = true
    82→            };
    83→
    84→            var levelData = JsonSerializer.Deserialize<BlenderLevelData>(jsonContent, options);
    85→
    86→            if (levelData?.Objects == null)
    87→            {
    88→                Logger.Warning("No objects found in Blender level data.");
    89→                return;
    90→            }
    91→
    92→            Logger.Info($"Loaded {levelData.Objects.Count} objects from Blender level.");
    93→
    94→            foreach (var obj in levelData.Objects)
    95→            {
    96→                switch (obj.Type)
    97→                {
    98→                    case "CAMERA":
    99→                        ProcessCamera(obj, levelData.ScaleFactor);
   100→                        break;
   101→                    case "MESH":
   102→                    case "GREASEPENCIL":
   103→                        // GREASEPENCIL objects are rendered to PNG and exported as planes
   104→                        // They are processed the same way as MESH objects
   105→                        ProcessMesh(obj);
   106→                        break;
   107→                    case "EMPTY":
   108→                        // Could be used for spawn points, triggers, etc.
   109→                        Logger.Debug($"Empty object: {obj.Name} at ({obj.Position?.X}, {obj.Position?.Y})");
   110→                        break;
   111→                    default:
   112→                        Logger.Warning($"Skipping unknown object type: {obj.Type}");
   113→                        break;
   114→                }
   115→            }
   116→
   117→            Logger.Info($"Created {_blenderEntities.Count} entities from Blender level.");
   118→        }
   119→        catch (Exception ex)
   120→        {
   121→            Logger.Error($"Error loading Blender level: {ex.Message}\n{ex.StackTrace}");
   122→        }
   123→    }
   124→
   125→    private void ProcessCamera(BlenderObject cameraObj, float scaleFactor)
   126→    {
   127→        if (cameraObj.Position == null) return;
   128→
   129→        var cameraPosition = new Vector2(cameraObj.Position.X, cameraObj.Position.Y);
   130→        _camera.Position = cameraPosition;
   131→
   132→        // Read ortho_scale from custom properties and convert to game zoom
   133→        // Blender ortho_scale = visible width in Blender units
   134→        // With scaleFactor, visible world pixels = ortho_scale * scaleFactor
   135→        // Game zoom = virtualWidth / visible_world_pixels
   136→        if (cameraObj.CustomProperties?.TryGetValue("zoom", out var zoomValue) == true)
   137→        {
   138→            if (zoomValue is JsonElement jsonElement && jsonElement.TryGetDouble(out var orthoScale))
   139→            {
   140→                // Convert Blender ortho_scale to game zoom
   141→                var visibleWorldWidth = orthoScale * scaleFactor;
   142→                var gameZoom = _camera.VirtualWidth / visibleWorldWidth;
   143→                _camera.Zoom = (float)gameZoom;
   144→                Logger.Debug($"Set camera zoom to {gameZoom} (from Blender ortho_scale {orthoScale}, scaleFactor {scaleFactor})");
   145→            }
   146→        }
   147→
   148→        Logger.Debug($"Set camera position to ({cameraPosition.X}, {cameraPosition.Y})");
   149→    }
   150→
   151→    private void ProcessMesh(BlenderObject meshObj)
   152→    {
   153→        if (meshObj.UvMapping == null)
   154→        {
   155→            Logger.Warning($"Skipping mesh '{meshObj.Name}' - no UV mapping.");
   156→            return;
   157→        }
   158→
   159→        // Extract content path from texture path
   160→        var contentPath = ExtractContentPath(meshObj.UvMapping.TexturePath);
   161→        if (string.IsNullOrEmpty(contentPath))
   162→        {
   163→            Logger.Warning($"Skipping mesh '{meshObj.Name}' - invalid texture path: {meshObj.UvMapping.TexturePath}");
   164→            return;
   165→        }
   166→
   167→        // Load texture
   168→        Texture2D texture;
   169→        if (!_loadedTextures.TryGetValue(contentPath, out texture))
   170→        {
   171→            try
   172→            {
   173→                texture = _content.Load<Texture2D>(contentPath);
   174→                _loadedTextures[contentPath] = texture;
   175→                Logger.Debug($"Loaded texture: {contentPath}");
   176→            }
   177→            catch (Exception ex)
   178→            {
   179→                Logger.Error($"Failed to load texture '{contentPath}': {ex.Message}");
   180→                return;
   181→            }
   182→        }
   183→
   184→        // Get UV coordinates from first layer
   185→        var uvLayer = meshObj.UvMapping.UvLayers?.FirstOrDefault();
   186→        if (uvLayer?.UvCoordinates == null || uvLayer.UvCoordinates.Count == 0)
   187→        {
   188→            Logger.Warning($"Skipping mesh '{meshObj.Name}' - no UV coordinates.");
   189→            return;
   190→        }
   191→
   192→        // Calculate source rectangle from UV coordinates
   193→        var sourceRect = CalculateSourceRect(uvLayer.UvCoordinates, texture);
   194→
   195→        // Create entity
   196→        var entity = _world.CreateEntity();
   197→        _blenderEntities.Add(entity);
   198→
   199→        // Position from Blender
   200→        var position = new Vector2(
   201→            meshObj.Position?.X ?? 0,
   202→            meshObj.Position?.Y ?? 0
   203→        );
   204→
   205→        // Transform
   206→        entity.Set(new Transform(position));
   207→
   208→        // SpriteInfo with calculated source rectangle
   209→        // Use mesh dimensions from Blender for Size (already scaled), fallback to source rect if not available
   210→        var size = new Vector2(
   211→            meshObj.Dimensions?.X ?? sourceRect.Width,
   212→            meshObj.Dimensions?.Y ?? sourceRect.Height
   213→        );
   214→
   215→        // Calculate origin from Blender's origin offset
   216→        // Blender origin offset is normalized (0-1) where (0,0) is bottom-left and (0.5,0.5) is center
   217→        // SpriteBatch.Draw expects origin in SOURCE TEXTURE coordinates (not destination size)
   218→        var originOffsetX = meshObj.OriginOffset?.X ?? 0.5f;  // Default to center
   219→        var originOffsetY = meshObj.OriginOffset?.Y ?? 0.5f;
   220→        // Y is inverted: Blender Y=0 is bottom, engine Y=0 is top
   221→        // Use sourceRect dimensions since SpriteBatch.Draw origin is in source coordinates
   222→        var origin = new Vector2(sourceRect.Width * originOffsetX, sourceRect.Height * (1 - originOffsetY));
   223→
   224→        entity.Set(new SpriteInfo
   225→        {
   226→            SpriteSheet = texture,
   227→            Source = sourceRect,
   228→            Size = size,
   229→            Color = Color.White,
   230→            Target = RenderTargetID.Main,
   231→            LayerDepth = DEFAULT_LAYER_DEPTH,
   232→            Origin = origin
   233→        });
   234→
   235→        // DrawComponent
   236→        entity.Set(new DrawComponent
   237→        {
   238→            Type = DrawElementType.Sprite,
   239→            Target = RenderTargetID.Main
   240→        });
   241→
   242→        // Make visible
   243→        entity.Set(new Visible());
   244→
   245→        // Process collection-based components
   246→        ProcessCollections(entity, meshObj, size);
   247→
   248→        Logger.Debug($"Created entity for mesh '{meshObj.Name}' at ({position.X}, {position.Y}) with size ({size.X}, {size.Y}), origin ({origin.X}, {origin.Y}), and source rect ({sourceRect.X}, {sourceRect.Y}, {sourceRect.Width}, {sourceRect.Height})");
   249→    }
   250→
   251→    /// <summary>
   252→    /// Extracts the MonoGame Content path from a full file system path.
   253→    /// Finds "/Content/" and returns the substring after it, without file extension.
   254→    /// </summary>
   255→    private static string ExtractContentPath(string fullPath)
   256→    {
   257→        if (string.IsNullOrEmpty(fullPath))
   258→            return null;
   259→
   260→        var contentIndex = fullPath.IndexOf("/Content/", StringComparison.OrdinalIgnoreCase);
   261→        if (contentIndex < 0)
   262→        {
   263→            // Try Windows-style path
   264→            contentIndex = fullPath.IndexOf("\\Content\\", StringComparison.OrdinalIgnoreCase);
   265→        }
   266→
   267→        if (contentIndex < 0)
   268→            return null;
   269→
   270→        var separatorLength = "/Content/".Length;
   271→        var relativePath = fullPath.Substring(contentIndex + separatorLength);
   272→
   273→        // Remove file extension
   274→        var extensionIndex = relativePath.LastIndexOf('.');
   275→        if (extensionIndex > 0)
   276→        {
   277→            relativePath = relativePath.Substring(0, extensionIndex);
   278→        }
   279→
   280→        return relativePath;
   281→    }
   282→
   283→    /// <summary>
   284→    /// Calculates a source rectangle from UV coordinates.
   285→    /// Assumes a plane mesh with 4 vertices.
   286→    /// </summary>
   287→    private static Rectangle CalculateSourceRect(List<UvCoordinate> uvCoords, Texture2D texture)
   288→    {
   289→        if (uvCoords == null || uvCoords.Count == 0)
   290→            return Rectangle.Empty;
   291→
   292→        float minU = uvCoords.Min(uv => uv.U);
   293→        float maxU = uvCoords.Max(uv => uv.U);
   294→        float minV = uvCoords.Min(uv => uv.V);
   295→        float maxV = uvCoords.Max(uv => uv.V);
   296→
   297→        // Convert UV to pixel coordinates with rounding for better precision
   298→        // UV origin is bottom-left, texture origin is top-left, so flip V
   299→        int x = (int)Math.Round(minU * texture.Width);
   300→        int y = (int)Math.Round((1 - maxV) * texture.Height);
   301→        int width = (int)Math.Round((maxU - minU) * texture.Width);
   302→        int height = (int)Math.Round((maxV - minV) * texture.Height);
   303→
   304→        return new Rectangle(x, y, width, height);
   305→    }
   306→
   307→    /// <summary>
   308→    /// Processes Blender collection membership and adds appropriate components.
   309→    /// Collection names determine entity behavior (e.g., "Collision" adds BoxCollider).
   310→    /// Collection custom properties can configure component settings.
   311→    /// </summary>
   312→    private void ProcessCollections(Entity entity, BlenderObject meshObj, Vector2 size)
   313→    {
   314→        if (meshObj.Collections == null || meshObj.Collections.Count == 0)
   315→            return;
   316→
   317→        // Compute visual offset from origin so collision boxes align with rendered sprites.
   318→        // SpriteBatch.Draw renders at: position - origin * scale
   319→        // In destination space: offset = -(size * originOffset)
   320→        var originOffsetX = meshObj.OriginOffset?.X ?? 0.5f;
   321→        var originOffsetY = meshObj.OriginOffset?.Y ?? 0.5f;
   322→        // Compute float edges of visual bounds relative to entity position
   323→        float left = -size.X * originOffsetX;
   324→        float top = -size.Y * (1 - originOffsetY);  // Y inverted: Blender Y=0 is bottom
   325→        // Snap to smallest enclosing integer rectangle (floor expands left/top, ceiling expands right/bottom)
   326→        int intLeft = (int)Math.Floor(left);
   327→        int intTop = (int)Math.Floor(top);
   328→        int intRight = (int)Math.Ceiling(left + size.X);
   329→        int intBottom = (int)Math.Ceiling(top + size.Y);
   330→
   331→        var boundsOffset = new Point(intLeft, intTop);
   332→        var boundsSize = new Point(intRight - intLeft, intBottom - intTop);
   333→
   334→        // Check for Collision collection
   335→        if (meshObj.Collections.Contains("Collision"))
   336→        {
   337→            entity.Set(new EntityInfo("Collision"));
   338→                
   339→            // Get collision layer from collection properties (default to -1 for all layers)
   340→            int layer = -1;
   341→            bool passive = false;
   342→
   343→            if (meshObj.CollectionProperties?.TryGetValue("Collision", out var collisionProps) == true)
   344→            {
   345→                layer = GetIntProperty(collisionProps, "layer", -1);
   346→                passive = GetBoolProperty(collisionProps, "passive", false);
   347→            }
   348→
   349→            var bounds = new Rectangle(boundsOffset, boundsSize);
   350→            var activeLayers = new HashSet<int> { layer };
   351→            entity.Set(new BoxCollider(bounds, activeLayers, passive));
   352→
   353→            Logger.Debug($"Added BoxCollider to '{meshObj.Name}' (layer: {layer}, passive: {passive})");
   354→        }
   355→
   356→        // Check for Player collection
   357→        if (meshObj.Collections.Contains("Player"))
   358→        {
   359→            entity.Set(new EntityInfo("Player"));
   360→            entity.Set(new BoxCollider(new Rectangle(boundsOffset, boundsSize)));
   361→            entity.Set(new RigidBody());
   362→            entity.Set(new Velocity());
   363→
   364→            // Add camera follow target component
   365→            entity.Set(new CameraFollowTarget
   366→            {
   367→                DampingX = 5.0f,
   368→                DampingY = 5.0f,
   369→                MaxDistanceX = 150.0f,
   370→                MaxDistanceY = 100.0f,
   371→                IsActive = true
   372→            });
   373→            Logger.Debug($"Set Player for '{meshObj.Name}'");
   374→        }
   375→
   376→        // Check for Enemy collection
   377→        if (meshObj.Collections.Contains("Enemy"))
   378→        {
   379→            entity.Set(new EntityInfo("Enemy"));
   380→            Logger.Debug($"Set Enemy for '{meshObj.Name}'");
   381→        }
   382→
   383→        // Check for Trigger collection (collision but no physics push)
   384→        if (meshObj.Collections.Contains("Trigger"))
   385→        {
   386→            if (!entity.Has<BoxCollider>())
   387→            {
   388→                var bounds = new Rectangle(boundsOffset, boundsSize);
   389→                entity.Set(new BoxCollider(bounds, passive: true));
   390→            }
   391→            entity.Set(new EntityInfo("Trigger"));
   392→            Logger.Debug($"Set as Trigger zone for '{meshObj.Name}'");
   393→        }
   394→
   395→        // Invoke registered game-specific collection handlers
   396→        foreach (var collection in meshObj.Collections)
   397→        {
   398→            if (_collectionHandlers.TryGetValue(collection, out var handler))
   399→            {
   400→                handler(entity);
   401→            }
   402→        }
   403→    }
   404→
   405→    /// <summary>
   406→    /// Gets an integer property from collection properties dictionary.
   407→    /// </summary>
   408→    private static int GetIntProperty(Dictionary<string, object> props, string key, int defaultValue)
   409→    {
   410→        if (props == null || !props.TryGetValue(key, out var value))
   411→            return defaultValue;
   412→
   413→        if (value is JsonElement jsonElement)
   414→        {
   415→            if (jsonElement.TryGetInt32(out var intVal))
   416→                return intVal;
   417→            if (jsonElement.TryGetDouble(out var doubleVal))
   418→                return (int)doubleVal;
   419→        }
   420→
   421→        if (value is int intValue)
   422→            return intValue;
   423→        if (value is double doubleValue)
   424→            return (int)doubleValue;
   425→        if (value is float floatValue)
   426→            return (int)floatValue;
   427→
   428→        return defaultValue;
   429→    }
   430→
   431→    /// <summary>
   432→    /// Gets a boolean property from collection properties dictionary.
   433→    /// </summary>
   434→    private static bool GetBoolProperty(Dictionary<string, object> props, string key, bool defaultValue)
   435→    {
   436→        if (props == null || !props.TryGetValue(key, out var value))
   437→            return defaultValue;
   438→
   439→        if (value is JsonElement jsonElement)
   440→        {
   441→            if (jsonElement.ValueKind == JsonValueKind.True)
   442→                return true;
   443→            if (jsonElement.ValueKind == JsonValueKind.False)
   444→                return false;
   445→        }
   446→
   447→        if (value is bool boolValue)
   448→            return boolValue;
   449→
   450→        return defaultValue;
   451→    }
   452→
   453→    private void CleanupEntities()
   454→    {
   455→        Logger.Info($"Cleaning up {_blenderEntities.Count} Blender entities...");
   456→        foreach (var entity in _blenderEntities.ToArray())
   457→        {
   458→            if (entity.IsAlive)
   459→            {
   460→                entity.Dispose();
   461→            }
   462→        }
   463→        _blenderEntities.Clear();
   464→    }
   465→
   466→    public void Update(GameState state)
   467→    {
   468→        // Processing happens via message subscription
   469→    }
   470→
   471→    public void Dispose()
   472→    {
   473→        CleanupEntities();
   474→        _loadedTextures.Clear();
   475→        GC.SuppressFinalize(this);
   476→    }
   477→}
