   119→        _dialogueState.IndicatorEntity.Set(new EntityInfo(nameof(EntityType.Interface)));
   120→        _dialogueState.IndicatorEntity.Set(new Transform(indicatorOffset) { Parent = _rootTransform });
   121→        _dialogueState.IndicatorEntity.Set(new SpriteInfo
   122→        {
   123→            SpriteSheet = indicatorTexture,
