   125→            Size = new Vector2(16, 16),
   126→            Color = Color.White,
   127→            Target = RenderTargetID.UI,
   128→            LayerDepth = 0.1f
   129→        });
   130→        _dialogueState.IndicatorEntity.Set(new DrawComponent
   131→        {
   132→            Type = DrawElementType.Sprite,
