   134→        });
   135→
   136→        // Set up Yarn runtime
   137→        var yarnProgram = content.Load<YarnProgram>("Dialogues/hello_world");
   138→
   139→        _dialogueRunner = new DialogueRunner();
   140→        _dialogueRunner.AddStringTable(yarnProgram);
