   162→
   163→        // Parse "Speaker: text" format
   164→        string? speaker = null;
   165→        var displayText = text;
   166→        var colonIndex = text.IndexOf(':');
   167→        if (colonIndex > 0 && colonIndex < 30)
   168→        {
   169→            speaker = text[..colonIndex].Trim();
   170→            displayText = text[(colonIndex + 1)..].Trim();
   171→        }
