   191→        _dialogueState.CurrentOptions.Clear();
   192→        _dialogueState.CurrentOptionIDs.Clear();
   193→        _dialogueState.SelectedOptionIndex = 0;
   194→        _dialogueState.WaitingForInput = true;
   195→
   196→        foreach (var option in optionSet.Options)
   197→        {
   198→            if (!option.IsAvailable) continue;
   199→            var text = _dialogueRunner.GetLocalizedTextForLine(option.Line);
   200→            _dialogueState.CurrentOptions.Add(text);
   201→            _dialogueState.CurrentOptionIDs.Add(option.ID);
   202→        }
   203→
   204→        // Clear the main text line
   205→        ref var dynamicText = ref _dialogueState.TextEntity.Get<DynamicText>();
   206→        dynamicText.TextContent = "";
   207→        dynamicText.VisibleCharacterCount = 0;
   208→
   209→        ShowOptions();
   210→        HideIndicator();
   211→    }
