    12→public class LayoutSystem(World world, ViewportManager renderer) : ISystem<GameState>
    13→{
    14→    private YogaNode _rootNode = new();
    15→    private Vector2 _rootPosition = new(-renderer.VirtualWidth / 2, -renderer.VirtualHeight / 2);
    16→
    17→    public void Update(GameState gameState)
    18→    {
    19→        var rootEntities = world.GetEntities()
    20→            .With((in LayoutNode n) => n.Parent == null)
    21→            .AsEnumerable();
    22→        var roots = rootEntities as Entity[] ?? rootEntities.ToArray();
    23→
    24→        _rootNode.Clear();
    25→        foreach (var rootEntity in roots)
    26→        {
    27→            BuildLayoutTree(rootEntity, _rootNode);
    28→        }
    29→
    30→        _rootNode.CalculateLayout();
    31→
    32→        foreach (var rootEntity in roots)
    33→        {
    34→            ApplyLayout(rootEntity, _rootPosition);
    35→        }
    36→    }
    37→
    38→    public bool IsEnabled { get; set; } = true;
    39→
    40→    private void BuildLayoutTree(in Entity entity, YogaNode parentNode)
    41→    {
    42→        ref var layoutNode = ref entity.Get<LayoutNode>();
    43→        
    44→        if (!parentNode.Contains(layoutNode.Node))
    45→        {
    46→            layoutNode.Node.Parent?.RemoveChild(layoutNode.Node);
    47→            parentNode.AddChild(layoutNode.Node);
    48→        }
    49→
    50→        // if (entity.Has<Size>())
    51→        // {
    52→        //     ref var size = ref entity.Get<Size>();
    53→        //     layoutNode.Node.Width = size.Width;
    54→        //     layoutNode.Node.Height = size.Height;
    55→        // }
    56→
    57→        // Recursively build tree for children
    58→        foreach (var child in layoutNode.Children)
    59→        {
    60→            BuildLayoutTree(child, layoutNode.Node);
    61→        }
    62→    }
    63→
    64→    private void ApplyLayout(in Entity entity, Vector2 parentPosition)
    65→    {
    66→        ref var layoutNode = ref entity.Get<LayoutNode>();
    67→        var position = new Vector2(
    68→            parentPosition.X + layoutNode.Node.LayoutX,
    69→            parentPosition.Y + layoutNode.Node.LayoutY
    70→        );
    71→
    72→        if (entity.Has<Transform>())
    73→        {
    74→            ref var entityTransform = ref entity.Get<Transform>();
    75→            entityTransform.Position = position;
    76→        }
    77→
    78→        foreach (var child in layoutNode.Children)
    79→        {
    80→            ApplyLayout(child, position);
    81→        }
    82→    }
    83→    
    84→    public void Dispose()
    85→    {
    86→        _rootNode.Clear();
    87→    }
    88→}

