     1→﻿using DefaultEcs;
     2→using Microsoft.Xna.Framework;
     3→using Microsoft.Xna.Framework.Content;
     4→using Microsoft.Xna.Framework.Graphics;
     5→using MonoDreams.Component;
     6→using MonoDreams.Component.Collision;
     7→using MonoDreams.Component.Input;
     8→using MonoDreams.Component.Physics;
     9→using MonoDreams.Draw;
    10→using MonoDreams.Examples.Component;
    11→using MonoDreams.Examples.Component.Camera;
    12→using MonoDreams.Examples.Component.Draw;
    13→using MonoDreams.Examples.Message.Level;
    14→
    15→namespace MonoDreams.Examples.EntityFactory;
    16→
    17→/// <summary>
    18→/// Factory for creating player entities with all necessary components
    19→/// </summary>
    20→public class PlayerEntityFactory(ContentManager content) : IEntityFactory
    21→{
    22→    private readonly Texture2D _charactersTileset = content.Load<Texture2D>("Atlas/TX Player");
    23→
    24→    public Entity CreateEntity(World world, in EntitySpawnRequest request)
    25→    {
    26→        var entity = world.CreateEntity();
    27→
    28→        // Add core components
    29→        entity.Set(new EntityInfo(EntityType.Player));
    30→        entity.Set(new PlayerState());
    31→        entity.Set(new Transform(request.Position));
    32→        entity.Set(new InputControlled());
    33→        // entity.Set(new BoxCollider(new Rectangle(Constants.PlayerOffset.ToPoint(), Constants.PlayerSize)));
    34→        entity.Set(new BoxCollider(new Rectangle(Point.Zero, Constants.PlayerSize)));
    35→        entity.Set(new RigidBody());
    36→        entity.Set(new Velocity());
    37→
    38→        // Add camera follow target component
    39→        entity.Set(new CameraFollowTarget
    40→        {
    41→            DampingX = 5.0f,  // Adjust for desired smoothness
    42→            DampingY = 5.0f,
    43→            MaxDistanceX = 150.0f,  // Maximum distance camera can lag behind
    44→            MaxDistanceY = 100.0f,
    45→            IsActive = true
    46→        });
    47→
    48→        // Add sprite information for rendering
    49→        entity.Set(new SpriteInfo
    50→        {
    51→            SpriteSheet = _charactersTileset,
    52→            Source = new Rectangle((int)request.TilesetPosition.X, (int)request.TilesetPosition.Y, 
    53→                                 (int)request.Size.X, (int)request.Size.Y),
    54→            Size = request.Size,
    55→            Color = Color.White * request.Layer._Opacity,
    56→            Target = RenderTargetID.Main,
    57→            LayerDepth = 0.1f,
    58→            Offset = Constants.PlayerSpriteOffset,
    59→        });
    60→        entity.Set(new DrawComponent
    61→        {
    62→            Type = DrawElementType.Sprite,
    63→            Target = RenderTargetID.Main,
    64→        });
    65→
    66→        // Process custom fields from LDtk
    67→        ProcessCustomFields(entity, request.CustomFields);
    68→
    69→        // Create orbiting orbs
    70→        var playerTransform = entity.Get<Transform>();
    71→        CreateBlueOrb(world, playerTransform);
    72→
    73→        return entity;
    74→    }
    75→
    76→    private void ProcessCustomFields(Entity entity, Dictionary<string, object> customFields)
    77→    {
    78→        // Handle player-specific custom fields from LDtk
    79→        if (customFields.TryGetValue("health", out var health) && health is int healthValue)
    80→        {
    81→            // Add health component if specified
    82→            // entity.Set(new Health(healthValue));
    83→        }
    84→
    85→        if (customFields.TryGetValue("speed", out var speed) && speed is float speedValue)
    86→        {
    87→            // Modify velocity or add speed component
    88→            // entity.Set(new MovementSpeed(speedValue));
    89→        }
    90→        
    91→        // Handle camera follow configuration from LDtk
    92→        if (customFields.TryGetValue("cameraDamping", out var damping) && damping is float dampingValue)
    93→        {
    94→            var followTarget = entity.Get<CameraFollowTarget>();
    95→            followTarget.DampingX = dampingValue;
    96→            followTarget.DampingY = dampingValue;
    97→            entity.Set(followTarget);
    98→        }
    99→    }
   100→
   101→    private void CreateBlueOrb(World world, Transform parentTransform)
   102→    {
   103→        var orbEntity = world.CreateEntity();
   104→
   105→        // Create transform parented to player
   106→        var orbTransform = new Transform(
   107→            position: new Vector2(50, 0),
   108→            rotation: 0f,
   109→            scale: Vector2.One
   110→        );
   111→        orbTransform.Parent = parentTransform;
   112→        orbEntity.Set(orbTransform);
   113→
   114→        // Add orbital motion component
   115→        orbEntity.Set(new OrbitalMotion
   116→        {
   117→            Angle = 0f,
   118→            Radius = 300f,
   119→            Speed = 1f,  // clockwise
   120→            CenterOffset = new Vector2(8, 16)
   121→        });
   122→
   123→        // Create blue circle mesh
   124→        var circleMesh = new CircleMeshGenerator(
   125→            center: Vector2.Zero,
   126→            radius: 9f,
   127→            color: Color.DodgerBlue,
   128→            segments: 20
   129→        ).Generate();
   130→
   131→        orbEntity.Set(new DrawComponent
   132→        {
   133→            Type = DrawElementType.Mesh,
   134→            Target = RenderTargetID.Main,
   135→            Vertices = circleMesh.Vertices,
   136→            Indices = circleMesh.Indices,
   137→            PrimitiveType = circleMesh.PrimitiveType,
   138→            LayerDepth = 0.991f
   139→        });
   140→        orbEntity.Set(new Visible());
   141→
   142→        // Create 3 red child orbs evenly spaced (0°, 120°, 240°)
   143→        CreateRedOrb(world, orbTransform, 0f);
   144→        CreateRedOrb(world, orbTransform, MathF.PI * 2f / 3f);      // 120°
   145→        CreateRedOrb(world, orbTransform, MathF.PI * 4f / 3f);      // 240°
   146→    }
   147→
   148→    private void CreateRedOrb(World world, Transform parentTransform, float startAngle)
   149→    {
   150→        var orbEntity = world.CreateEntity();
   151→        var rnd = new Random();
   152→
   153→        // Create transform parented to blue orb
   154→        var orbTransform = new Transform(
   155→            position: new Vector2(20, 0),
   156→            rotation: 0f,
   157→            scale: Vector2.One
   158→        );
   159→        orbTransform.Parent = parentTransform;
   160→        orbEntity.Set(orbTransform);
   161→
   162→        // Add orbital motion component with starting angle offset
   163→        orbEntity.Set(new OrbitalMotion
   164→        {
   165→            Angle = startAngle,
   166→            Radius = 20f,
   167→            Speed = 5.0f + (rnd.NextSingle() * (7.0f - 5.0f)),  // clockwise, same direction as parent
   168→            CenterOffset = Vector2.Zero
   169→        });
   170→
   171→        // Create red circle mesh (smaller)
   172→        var circleMesh = new CircleMeshGenerator(
   173→            center: Vector2.Zero,
   174→            radius: 4f,
   175→            color: Color.Red,
   176→            segments: 16
   177→        ).Generate();
   178→
   179→        orbEntity.Set(new DrawComponent
   180→        {
   181→            Type = DrawElementType.Mesh,
   182→            Target = RenderTargetID.Main,
   183→            Vertices = circleMesh.Vertices,
   184→            Indices = circleMesh.Indices,
   185→            PrimitiveType = circleMesh.PrimitiveType,
   186→            LayerDepth = 0.992f
   187→        });
   188→        orbEntity.Set(new Visible());
   189→    }
