     1→using System.Text.Json;
     2→using DefaultEcs;
     3→using DefaultEcs.System;
     4→using Microsoft.Xna.Framework;
     5→using Microsoft.Xna.Framework.Content;
     6→using Microsoft.Xna.Framework.Graphics;
     7→using MonoDreams.Component;
     8→using MonoDreams.Component.Collision;
     9→using MonoDreams.Component.Input;
    10→using MonoDreams.Component.Physics;
    11→using MonoDreams.Examples.Component;
    12→using MonoDreams.Examples.Component.Camera;
    13→using MonoDreams.Examples.Component.Draw;
    14→using MonoDreams.Examples.Level;
    15→using MonoDreams.Examples.Message.Level;
    16→using MonoDreams.State;
    17→
    18→namespace MonoDreams.Examples.System.Level;
    19→
    20→/// <summary>
    21→/// Parses Blender-exported JSON level files and creates entities for meshes.
    22→/// Handles LoadLevelRequest messages where the identifier starts with "Blender_".
    23→/// </summary>
    24→public sealed class BlenderLevelParserSystem : ISystem<GameState>
    25→{
    26→    private const float DEFAULT_LAYER_DEPTH = 0.5f;
    27→
    28→    private readonly World _world;
    29→    private readonly ContentManager _content;
    30→    private readonly MonoDreams.Component.Camera _camera;
    31→    private readonly Dictionary<string, Texture2D> _loadedTextures = new();
    32→    private readonly HashSet<Entity> _blenderEntities = new();
    33→
    34→    public bool IsEnabled { get; set; } = true;
    35→
    36→    public BlenderLevelParserSystem(World world, ContentManager content, MonoDreams.Component.Camera camera)
    37→    {
    38→        _world = world ?? throw new ArgumentNullException(nameof(world));
    39→        _content = content ?? throw new ArgumentNullException(nameof(content));
    40→        _camera = camera ?? throw new ArgumentNullException(nameof(camera));
    41→
    42→        _world.Subscribe<LoadLevelRequest>(OnLoadLevelRequest);
    43→    }
    44→
    45→    [Subscribe]
    46→    private void OnLoadLevelRequest(in LoadLevelRequest request)
    47→    {
    48→        if (!IsEnabled) return;
    49→
    50→        // Only handle Blender levels
    51→        if (!request.LevelIdentifier.StartsWith("Blender_"))
    52→            return;
    53→
    54→        Console.WriteLine($"Loading Blender level: {request.LevelIdentifier}");
    55→
    56→        // Clean up previous entities
    57→        CleanupEntities();
    58→
    59→        try
    60→        {
    61→            // Load JSON file from Content directory
    62→            // The Content.RootDirectory gives us the path to the Content folder
    63→            var jsonPath = Path.Combine(AppDomain.CurrentDomain.BaseDirectory, _content.RootDirectory, "blender_level.json");
    64→            Console.WriteLine($"Loading Blender level from: {jsonPath}");
    65→            var jsonContent = File.ReadAllText(jsonPath);
    66→
    67→            var options = new JsonSerializerOptions
    68→            {
    69→                PropertyNameCaseInsensitive = true
    70→            };
    71→
    72→            var levelData = JsonSerializer.Deserialize<BlenderLevelData>(jsonContent, options);
    73→
    74→            if (levelData?.Objects == null)
    75→            {
    76→                Console.WriteLine("No objects found in Blender level data.");
    77→                return;
    78→            }
    79→
    80→            Console.WriteLine($"Loaded {levelData.Objects.Count} objects from Blender level.");
    81→
    82→            foreach (var obj in levelData.Objects)
    83→            {
    84→                switch (obj.Type)
    85→                {
    86→                    case "CAMERA":
    87→                        ProcessCamera(obj, levelData.ScaleFactor);
    88→                        break;
    89→                    case "MESH":
    90→                    case "GREASEPENCIL":
    91→                        // GREASEPENCIL objects are rendered to PNG and exported as planes
    92→                        // They are processed the same way as MESH objects
    93→                        ProcessMesh(obj);
    94→                        break;
    95→                    case "EMPTY":
    96→                        // Could be used for spawn points, triggers, etc.
    97→                        Console.WriteLine($"Empty object: {obj.Name} at ({obj.Position?.X}, {obj.Position?.Y})");
    98→                        break;
    99→                    default:
   100→                        Console.WriteLine($"Skipping object type: {obj.Type}");
   101→                        break;
   102→                }
   103→            }
   104→
   105→            Console.WriteLine($"Created {_blenderEntities.Count} entities from Blender level.");
   106→        }
   107→        catch (Exception ex)
   108→        {
   109→            Console.WriteLine($"Error loading Blender level: {ex.Message}\n{ex.StackTrace}");
   110→        }
   111→    }
   112→
   113→    private void ProcessCamera(BlenderObject cameraObj, float scaleFactor)
   114→    {
   115→        if (cameraObj.Position == null) return;
   116→
   117→        var cameraPosition = new Vector2(cameraObj.Position.X, cameraObj.Position.Y);
   118→        _camera.Position = cameraPosition;
   119→
   120→        // Read ortho_scale from custom properties and convert to game zoom
   121→        // Blender ortho_scale = visible width in Blender units
   122→        // With scaleFactor, visible world pixels = ortho_scale * scaleFactor
   123→        // Game zoom = virtualWidth / visible_world_pixels
   124→        if (cameraObj.CustomProperties?.TryGetValue("zoom", out var zoomValue) == true)
   125→        {
   126→            if (zoomValue is JsonElement jsonElement && jsonElement.TryGetDouble(out var orthoScale))
   127→            {
   128→                // Convert Blender ortho_scale to game zoom
   129→                var visibleWorldWidth = orthoScale * scaleFactor;
   130→                var gameZoom = _camera.VirtualWidth / visibleWorldWidth;
   131→                _camera.Zoom = (float)gameZoom;
   132→                Console.WriteLine($"Set camera zoom to {gameZoom} (from Blender ortho_scale {orthoScale}, scaleFactor {scaleFactor})");
   133→            }
   134→        }
   135→
   136→        Console.WriteLine($"Set camera position to ({cameraPosition.X}, {cameraPosition.Y})");
   137→    }
   138→
   139→    private void ProcessMesh(BlenderObject meshObj)
   140→    {
   141→        if (meshObj.UvMapping == null)
   142→        {
   143→            Console.WriteLine($"Skipping mesh '{meshObj.Name}' - no UV mapping.");
   144→            return;
   145→        }
   146→
   147→        // Extract content path from texture path
   148→        var contentPath = ExtractContentPath(meshObj.UvMapping.TexturePath);
   149→        if (string.IsNullOrEmpty(contentPath))
   150→        {
   151→            Console.WriteLine($"Skipping mesh '{meshObj.Name}' - invalid texture path: {meshObj.UvMapping.TexturePath}");
   152→            return;
   153→        }
   154→
   155→        // Load texture
   156→        Texture2D texture;
   157→        if (!_loadedTextures.TryGetValue(contentPath, out texture))
   158→        {
   159→            try
   160→            {
   161→                texture = _content.Load<Texture2D>(contentPath);
   162→                _loadedTextures[contentPath] = texture;
   163→                Console.WriteLine($"Loaded texture: {contentPath}");
   164→            }
   165→            catch (Exception ex)
   166→            {
   167→                Console.WriteLine($"Failed to load texture '{contentPath}': {ex.Message}");
   168→                return;
   169→            }
   170→        }
   171→
   172→        // Get UV coordinates from first layer
   173→        var uvLayer = meshObj.UvMapping.UvLayers?.FirstOrDefault();
   174→        if (uvLayer?.UvCoordinates == null || uvLayer.UvCoordinates.Count == 0)
   175→        {
   176→            Console.WriteLine($"Skipping mesh '{meshObj.Name}' - no UV coordinates.");
   177→            return;
   178→        }
   179→
   180→        // Calculate source rectangle from UV coordinates
   181→        var sourceRect = CalculateSourceRect(uvLayer.UvCoordinates, texture);
   182→
   183→        // Create entity
   184→        var entity = _world.CreateEntity();
   185→        _blenderEntities.Add(entity);
   186→
   187→        // Position from Blender
   188→        var position = new Vector2(
   189→            meshObj.Position?.X ?? 0,
   190→            meshObj.Position?.Y ?? 0
   191→        );
   192→
   193→        // Transform
   194→        entity.Set(new Transform(position));
   195→
   196→        // SpriteInfo with calculated source rectangle
   197→        // Use mesh dimensions from Blender for Size (already scaled), fallback to source rect if not available
   198→        var size = new Vector2(
   199→            meshObj.Dimensions?.X ?? sourceRect.Width,
   200→            meshObj.Dimensions?.Y ?? sourceRect.Height
   201→        );
   202→
   203→        // Calculate origin from Blender's origin offset
   204→        // Blender origin offset is normalized (0-1) where (0,0) is bottom-left and (0.5,0.5) is center
   205→        // SpriteBatch.Draw expects origin in SOURCE TEXTURE coordinates (not destination size)
   206→        var originOffsetX = meshObj.OriginOffset?.X ?? 0.5f;  // Default to center
   207→        var originOffsetY = meshObj.OriginOffset?.Y ?? 0.5f;
   208→        // Y is inverted: Blender Y=0 is bottom, engine Y=0 is top
   209→        // Use sourceRect dimensions since SpriteBatch.Draw origin is in source coordinates
   210→        var origin = new Vector2(sourceRect.Width * originOffsetX, sourceRect.Height * (1 - originOffsetY));
   211→
   212→        entity.Set(new SpriteInfo
   213→        {
   214→            SpriteSheet = texture,
   215→            Source = sourceRect,
   216→            Size = size,
   217→            Color = Color.White,
   218→            Target = RenderTargetID.Main,
   219→            LayerDepth = DEFAULT_LAYER_DEPTH,
   220→            Origin = origin
   221→        });
   222→
   223→        // DrawComponent
   224→        entity.Set(new DrawComponent
   225→        {
   226→            Type = DrawElementType.Sprite,
   227→            Target = RenderTargetID.Main
   228→        });
   229→
   230→        // Make visible
   231→        entity.Set(new Visible());
   232→
   233→        // Process collection-based components
   234→        ProcessCollections(entity, meshObj, size);
   235→
   236→        Console.WriteLine($"Created entity for mesh '{meshObj.Name}' at ({position.X}, {position.Y}) with size ({size.X}, {size.Y}), origin ({origin.X}, {origin.Y}), and source rect ({sourceRect.X}, {sourceRect.Y}, {sourceRect.Width}, {sourceRect.Height})");
   237→    }
   238→
   239→    /// <summary>
   240→    /// Extracts the MonoGame Content path from a full file system path.
   241→    /// Finds "/Content/" and returns the substring after it, without file extension.
   242→    /// </summary>
   243→    private static string ExtractContentPath(string fullPath)
   244→    {
   245→        if (string.IsNullOrEmpty(fullPath))
   246→            return null;
   247→
   248→        var contentIndex = fullPath.IndexOf("/Content/", StringComparison.OrdinalIgnoreCase);
   249→        if (contentIndex < 0)
   250→        {
   251→            // Try Windows-style path
   252→            contentIndex = fullPath.IndexOf("\\Content\\", StringComparison.OrdinalIgnoreCase);
   253→        }
   254→
   255→        if (contentIndex < 0)
   256→            return null;
   257→
   258→        var separatorLength = "/Content/".Length;
   259→        var relativePath = fullPath.Substring(contentIndex + separatorLength);
   260→
   261→        // Remove file extension
   262→        var extensionIndex = relativePath.LastIndexOf('.');
   263→        if (extensionIndex > 0)
   264→        {
   265→            relativePath = relativePath.Substring(0, extensionIndex);
   266→        }
   267→
   268→        return relativePath;
   269→    }
   270→
   271→    /// <summary>
   272→    /// Calculates a source rectangle from UV coordinates.
   273→    /// Assumes a plane mesh with 4 vertices.
   274→    /// </summary>
   275→    private static Rectangle CalculateSourceRect(List<UvCoordinate> uvCoords, Texture2D texture)
   276→    {
   277→        if (uvCoords == null || uvCoords.Count == 0)
   278→            return Rectangle.Empty;
   279→
   280→        float minU = uvCoords.Min(uv => uv.U);
   281→        float maxU = uvCoords.Max(uv => uv.U);
   282→        float minV = uvCoords.Min(uv => uv.V);
   283→        float maxV = uvCoords.Max(uv => uv.V);
   284→
   285→        // Convert UV to pixel coordinates with rounding for better precision
   286→        // UV origin is bottom-left, texture origin is top-left, so flip V
   287→        int x = (int)Math.Round(minU * texture.Width);
   288→        int y = (int)Math.Round((1 - maxV) * texture.Height);
   289→        int width = (int)Math.Round((maxU - minU) * texture.Width);
   290→        int height = (int)Math.Round((maxV - minV) * texture.Height);
   291→
   292→        return new Rectangle(x, y, width, height);
   293→    }
   294→
   295→    /// <summary>
   296→    /// Processes Blender collection membership and adds appropriate components.
   297→    /// Collection names determine entity behavior (e.g., "Collision" adds BoxCollider).
   298→    /// Collection custom properties can configure component settings.
   299→    /// </summary>
   300→    private void ProcessCollections(Entity entity, BlenderObject meshObj, Vector2 size)
   301→    {
