    79→    public void Load(ScreenController screenController, ContentManager content)
    80→    {
    81→        _screenController = screenController;
    82→
    83→        // Subscribe to screen transition requests
    84→        _world.Subscribe<ScreenTransitionRequest>(OnScreenTransitionRequest);
    85→
    86→        var cursorTextures = new Dictionary<CursorType, Texture2D>
    87→        {
    88→            [CursorType.Default] = content.Load<Texture2D>("Mouse sprites/Triangle Mouse icon 1"),
