   100→    [Subscribe]
   101→    private void OnScreenTransitionRequest(in ScreenTransitionRequest request)
   102→    {
   103→        // Store the requested level in the world so the next screen can access it
   104→        if (request.LevelIdentifier != null)
   105→        {
   106→            _screenController?.Game.Services.AddService(new RequestedLevelComponent(request.LevelIdentifier));
   107→        }
   108→
   109→        // Transition to the requested screen
   110→        _screenController?.LoadScreen(request.ScreenName);
   111→    }
   112→
   113→    private void CreateLevelSelectionUI()
   114→    {
   115→        // Lofi color palette
   116→        var darkBrown = new Color(60, 50, 45);        // Main text color
   117→        var terracotta = new Color(200, 120, 80);     // Hover/accent color
   118→        var mutedBrown = new Color(150, 140, 130);    // Disabled color
   119→
   120→        // Create button style
   121→        var buttonStyle = ButtonStyle.WithColors(darkBrown, terracotta, mutedBrown);
   122→
   123→        // Create entities first
   124→        var titleEntity = CreateTextEntity("Select Level", _font, darkBrown, scale: 0.3f, DrawLayerDepth.GetLayerDepth(DrawLayer.Title));
   125→        var button1 = CreateButtonEntity("Level 1", _font, 0, "Level_0", true, buttonStyle);
   126→        var button2 = CreateButtonEntity("Level 2", _font, 1, "Blender_Level", true, buttonStyle);
   127→        var button3 = CreateButtonEntity("Level 3", _font, 2, "Level_0", false, buttonStyle);
   128→
   129→        // Create UI using auto layout with slots
   130→        var layout = new AutoLayoutBuilder(_world, _viewportManager);
   131→
   132→        layout.CreateRoot(ScreenAnchor.Center)
   133→            .Name("Root")
   134→            .Direction(LayoutDirection.Vertical)
   135→            .Gap(40)
   136→            .AlignMain(MainAxisAlignment.Center)
   137→            .AlignCross(CrossAxisAlignment.Center)
   138→            .AddSlot(slot => slot
   139→                .Attach(titleEntity)
   140→                .MeasureWith(MeasureText))
   141→            .AddContainer(row => row
   142→                .Name("ButtonColumn")
   143→                .Direction(LayoutDirection.Vertical)
   144→                .Gap(50)
   145→                .AlignCross(CrossAxisAlignment.Center)
   146→                .AddSlot(slot => slot.Attach(button1.container).MeasureWith(_ => button1.size))
   147→                .AddSlot(slot => slot.Attach(button2.container).MeasureWith(_ => button2.size))
   148→                .AddSlot(slot => slot.Attach(button3.container).MeasureWith(_ => button3.size))
   149→            )
   150→            .Build();
   151→    }
   152→
   153→    private Entity CreateTextEntity(string text, BitmapFont font, Color color, float scale, float layerDepth)
   154→    {
   155→        var entity = _world.CreateEntity();
   156→        entity.Set(new Transform(Vector2.Zero));
   157→        entity.Set(new DynamicText
   158→        {
   159→            Target = RenderTargetID.Main,
   160→            LayerDepth = layerDepth,
   161→            TextContent = text,
   162→            Font = font,
   163→            Color = color,
   164→            Scale = scale,
   165→            IsRevealed = true,
   166→            VisibleCharacterCount = int.MaxValue
   167→        });
   168→        return entity;
   169→    }
   170→
   171→    private (Entity container, Vector2 size) CreateButtonEntity(
   172→        string text,
   173→        BitmapFont font,
   174→        int levelIndex,
   175→        string levelName,
   176→        bool isClickable,
   177→        ButtonStyle style)
   178→    {
   179→        // Measure text to determine button size
   180→        var textSize = font.MeasureString(text) * style.TextScale;
   181→        var buttonSize = new Vector2(
   182→            textSize.Width + style.Padding * 2,
   183→            textSize.Height + style.Padding * 2);
   184→
   185→        // Create button container entity
   186→        var buttonContainerEntity = _world.CreateEntity();
   187→        var buttonTransform = new Transform(Vector2.Zero);
   188→        buttonContainerEntity.Set(buttonTransform);
   189→
   190→        // Create button text entity with its own transform, offset by padding to center text
   191→        var buttonTextEntity = _world.CreateEntity();
   192→        var textTransform = new Transform(new Vector2(style.Padding, style.Padding));
   193→        textTransform.Parent = buttonTransform;  // Child of button container transform
   194→        buttonTextEntity.Set(textTransform);
   195→        buttonTextEntity.Set(new DynamicText
   196→        {
   197→            Target = RenderTargetID.Main,
   198→            LayerDepth = DrawLayerDepth.GetLayerDepth(DrawLayer.ButtonText),
   199→            TextContent = text,
   200→            Font = font,
   201→            Color = isClickable ? style.DefaultColor : style.DisabledColor,
   202→            Scale = style.TextScale,
   203→            IsRevealed = true,
   204→            VisibleCharacterCount = int.MaxValue
   205→        });
   206→
   207→        // Create outline entity (shares transform with button)
   208→        var outlineEntity = _world.CreateEntity();
   209→        outlineEntity.Set(buttonTransform); // Share transform
   210→        outlineEntity.Set(new SimpleButton
   211→        {
   212→            Size = buttonSize,
   213→            LineThickness = style.BorderThickness,
   214→            Color = style.BorderColor,
   215→            TextEntity = buttonTextEntity,
   216→            Target = RenderTargetID.Main
   217→        });
   218→        outlineEntity.Set(new LevelSelector
   219→        {
   220→            LevelIndex = levelIndex,
   221→            LevelName = levelName,
   222→            IsClickable = isClickable,
   223→            IsHovered = false,
   224→            DefaultColor = style.DefaultColor,
   225→            HoveredColor = style.HoveredColor,
   226→            DisabledColor = style.DisabledColor
   227→        });
   228→
   229→        return (buttonContainerEntity, buttonSize);
   230→    }
   231→
   232→    private static Vector2 MeasureText(Entity entity)
   233→    {
   234→        if (!entity.Has<DynamicText>()) return Vector2.Zero;
   235→
   236→        ref var text = ref entity.Get<DynamicText>();
   237→        var measuredSize = text.Font.MeasureString(text.TextContent);
   238→        return new Vector2(measuredSize.Width * text.Scale, measuredSize.Height * text.Scale);
   239→    }
