    12→/// <summary>
    13→/// Prepares DrawComponents with mesh data for button outlines.
    14→/// This system is game-agnostic and generates generic vertex buffer data.
    15→/// </summary>
    16→[With(typeof(SimpleButton), typeof(Transform))]
    17→public class ButtonMeshPrepSystem(World world) : AEntitySetSystem<GameState>(world)
    18→{
    19→    protected override void Update(GameState state, in Entity entity)
    20→    {
    21→        ref readonly var outline = ref entity.Get<SimpleButton>();
    22→        ref readonly var transform = ref entity.Get<Transform>();
    23→
    24→        var vertices = new List<VertexPositionColor>();
    25→        var indices = new List<int>();
    26→        var indexOffset = 0;
    27→
    28→        // Calculate corners of the button using world position as top-left origin
    29→        // (Layout system positions elements at their top-left corner)
    30→        var position = transform.WorldPosition;
    31→
    32→        // Top-left, top-right, bottom-right, bottom-left corners
    33→        var topLeft = position;
    34→        var topRight = new Vector2(position.X + outline.Size.X, position.Y);
    35→        var bottomRight = position + outline.Size;
    36→        var bottomLeft = new Vector2(position.X, position.Y + outline.Size.Y);
    37→
    38→        // Create thick lines for the four sides of the rectangle
    39→        AddThickLine(vertices, indices, topLeft, topRight, outline.LineThickness, outline.Color, ref indexOffset);
    40→        AddThickLine(vertices, indices, topRight, bottomRight, outline.LineThickness, outline.Color, ref indexOffset);
    41→        AddThickLine(vertices, indices, bottomRight, bottomLeft, outline.LineThickness, outline.Color, ref indexOffset);
    42→        AddThickLine(vertices, indices, bottomLeft, topLeft, outline.LineThickness, outline.Color, ref indexOffset);
    43→
    44→        // Set or update the DrawComponent
    45→        if (!entity.Has<DrawComponent>())
    46→        {
    47→            entity.Set(new DrawComponent
    48→            {
    49→                Type = DrawElementType.Mesh,
    50→                Vertices = vertices.ToArray(),
    51→                Indices = indices.ToArray(),
    52→                PrimitiveType = PrimitiveType.TriangleList,
    53→                Target = outline.Target,
    54→                LayerDepth = 0.95f
    55→            });
    56→        }
    57→        else
    58→        {
    59→            ref var drawComponent = ref entity.Get<DrawComponent>();
    60→            drawComponent.Type = DrawElementType.Mesh;
    61→            drawComponent.Vertices = vertices.ToArray();
    62→            drawComponent.Indices = indices.ToArray();
    63→            drawComponent.PrimitiveType = PrimitiveType.TriangleList;
    64→            drawComponent.Target = outline.Target;
    65→        }
    66→    }
    67→
    68→    private void AddThickLine(List<VertexPositionColor> vertices, List<int> indices,
    69→        Vector2 start, Vector2 end, float thickness, Color color, ref int indexOffset)
    70→    {
    71→        // Calculate direction and perpendicular vector for thickness
    72→        Vector2 direction = end - start;
    73→        Vector2 perpendicular = new Vector2(-direction.Y, direction.X);
    74→        perpendicular.Normalize();
    75→        perpendicular *= thickness / 2;
    76→
    77→        // Create the four corners of the line segment
    78→        vertices.Add(new VertexPositionColor(new Vector3(start + perpendicular, 0), color));
    79→        vertices.Add(new VertexPositionColor(new Vector3(start - perpendicular, 0), color));
    80→        vertices.Add(new VertexPositionColor(new Vector3(end - perpendicular, 0), color));
    81→        vertices.Add(new VertexPositionColor(new Vector3(end + perpendicular, 0), color));
    82→
    83→        // Create two triangles to form the line
    84→        indices.Add(indexOffset);
    85→        indices.Add(indexOffset + 1);
    86→        indices.Add(indexOffset + 2);
    87→
    88→        indices.Add(indexOffset);
    89→        indices.Add(indexOffset + 2);
    90→        indices.Add(indexOffset + 3);
    91→
    92→        indexOffset += 4;
    93→    }
    94→}
