     9→
    10→namespace MonoDreams.Examples.System.Draw;
    11→
    12→/// <summary>
    13→/// Unified rendering system that handles all draw types: sprites, text, nine-patch, and mesh.
    14→/// This system is game-agnostic - it only renders what's in DrawComponent without any
    15→/// specialized component handling.
    16→/// </summary>
    17→public class MasterRenderSystem(
    18→    SpriteBatch spriteBatch,
    19→    GraphicsDevice graphicsDevice,
    20→    MonoDreams.Component.Camera camera,
    21→    IReadOnlyDictionary<RenderTargetID, RenderTarget2D> renderTargets,
    22→    World world) : ISystem<GameState>
    23→{
    24→    private BasicEffect? _basicEffect;
    25→
    26→    private enum BatchType { None, Sprite, Mesh }
    27→
    28→    private static BatchType GetBatchType(DrawElementType type) =>
    29→        type == DrawElementType.Mesh ? BatchType.Mesh : BatchType.Sprite;
    30→
    31→    private void EnsureBasicEffect()
    32→    {
    33→        _basicEffect ??= new BasicEffect(graphicsDevice)
    34→        {
    35→            VertexColorEnabled = true,
    36→            View = Matrix.Identity,
    37→            Projection = Matrix.CreateOrthographicOffCenter(0, camera.VirtualWidth, camera.VirtualHeight, 0, 0, 1)
    38→        };
    39→    }
    40→
    41→    private void BeginSpriteBatch(Matrix transformMatrix)
    42→    {
    43→        spriteBatch.Begin(
    44→            sortMode: SpriteSortMode.Deferred, // We pre-sort, so no need for FrontToBack
    45→            blendState: BlendState.AlphaBlend,
    46→            samplerState: SamplerState.LinearClamp,
    47→            depthStencilState: DepthStencilState.None,
    48→            rasterizerState: RasterizerState.CullNone,
    49→            effect: null,
    50→            transformMatrix: transformMatrix);
    51→    }
    52→
    53→    private void EndSpriteBatch() => spriteBatch.End();
    54→
    55→    private void ResetGraphicsStateForMeshRendering()
    56→    {
    57→        // Reset states after SpriteBatch.End() before mesh rendering
    58→        graphicsDevice.BlendState = BlendState.AlphaBlend;
    59→        graphicsDevice.DepthStencilState = DepthStencilState.None;
    60→        graphicsDevice.RasterizerState = RasterizerState.CullNone;
    61→        graphicsDevice.SamplerStates[0] = SamplerState.PointClamp;
    62→    }
    63→
    64→    public void Update(GameState state)
    65→    {
    66→        EnsureBasicEffect();
    67→
    68→        foreach (var renderTarget in renderTargets)
    69→        {
    70→            var queryBuilder = world.GetEntities()
    71→                .With((in DrawComponent e) => e.Target == renderTarget.Key);
    72→
    73→            // Main target: respect CullingSystem visibility. HUD/UI: always render.
    74→            if (renderTarget.Key == RenderTargetID.Main)
    75→                queryBuilder.With<Visible>();
    76→
    77→            var drawList = queryBuilder.AsSet();
    78→
    79→            graphicsDevice.SetRenderTarget(renderTarget.Value);
    80→            graphicsDevice.Clear(Color.Transparent);
    81→
    82→            var transformMatrix = Matrix.Identity;
    83→            if (renderTarget.Key == RenderTargetID.Main)
    84→            {
    85→                transformMatrix = camera.GetViewTransformationMatrix();
    86→            }
    87→
    88→            // Sort ALL entities by LayerDepth (stable sort preserves order for same depth)
    89→            var entities = drawList.GetEntities().ToArray();
    90→            var sortedEntities = entities
    91→                .Select((entity, index) => (entity, index, dc: entity.Get<DrawComponent>()))
    92→                .Where(x => x.dc.Type != DrawElementType.Mesh || x.dc.HasValidMesh)
    93→                .OrderBy(x => x.dc.LayerDepth)
    94→                .ThenBy(x => x.index) // Stable sort
    95→                .ToList();
    96→
    97→            if (sortedEntities.Count > 0)
    98→            {
    99→                RenderInterleaved(sortedEntities, transformMatrix);
   100→            }

