     9→/// <summary>
    10→/// Unified drawing component that supports multiple rendering methods:
    11→/// sprites, text, nine-patch, and mesh (vertex buffer) rendering.
    12→/// </summary>
    13→public class DrawComponent
    14→{
    15→    public DrawElementType Type;
    16→    public RenderTargetID Target;
    17→
    18→    // Transform fields for all types
    19→    public Vector2 Position;
    20→    public float Rotation;
    21→    public Vector2 Origin;
    22→    public Vector2 Scale = Vector2.One;
    23→    public Color Color = Color.White;
    24→    public float LayerDepth;
    25→
    26→    // Sprite specific
    27→    public Texture2D? Texture;
    28→    public Rectangle? SourceRectangle;
    29→    public Vector2 Size;
    30→
    31→    // Text specific
    32→    public BitmapFont? Font;
    33→    public string? Text;
    34→
    35→    // NinePatch specific
    36→    public NinePatchInfo? NinePatchData;
    37→
    38→    // Mesh specific (for vertex buffer rendering)
    39→    public VertexPositionColor[]? Vertices;
    40→    public int[]? Indices;
    41→    public PrimitiveType PrimitiveType = PrimitiveType.TriangleList;
    42→    public Matrix? WorldMatrix; // Transform matrix for mesh rendering
    43→
    44→    /// <summary>
    45→    /// Sets the mesh data from a MeshData struct.
    46→    /// </summary>
    47→    public void SetMeshData(MeshData meshData)
    48→    {
    49→        Type = DrawElementType.Mesh;
    50→        Vertices = meshData.Vertices;
    51→        Indices = meshData.Indices;
    52→        PrimitiveType = meshData.PrimitiveType;
    53→    }
    54→
    55→    /// <summary>
    56→    /// Sets the mesh data from a mesh generator.
    57→    /// </summary>
    58→    public void SetMeshData(IMeshGenerator generator)
    59→    {
    60→        SetMeshData(generator.Generate());
    61→    }
    62→
    63→    /// <summary>
    64→    /// Gets the primitive count for mesh rendering.
    65→    /// </summary>
    66→    public int GetPrimitiveCount()
    67→    {
    68→        if (Indices == null || Indices.Length == 0) return 0;
    69→
    70→        return PrimitiveType switch
    71→        {
    72→            PrimitiveType.TriangleList => Indices.Length / 3,
    73→            PrimitiveType.TriangleStrip => Indices.Length - 2,
    74→            PrimitiveType.LineList => Indices.Length / 2,
    75→            PrimitiveType.LineStrip => Indices.Length - 1,
    76→            _ => 0
    77→        };
    78→    }
    79→
    80→    public bool HasValidMesh => Vertices is { Length: > 0 } && Indices is { Length: > 0 };
    81→}

