   241→    private SequentialSystem<GameState> CreateUpdateSystem()
   242→    {
   243→        var inputSystems = new CursorInputSystem(_world);
   244→
   245→        // Layout systems must run before UI systems to position elements
   246→        var layoutSystems = new SequentialSystem<GameState>(
   247→            new IntrinsicSizingSystem(_world),     // Measure content sizes via callbacks
   248→            new AutoLayoutSystem(_world, _viewportManager),  // Calculate and apply layout
   249→            new LayoutDebugSystem(_world, _font, _camera)   // Debug visualization (toggle with LayoutDebugSystem.Enabled)
   250→        );
   251→
   252→        var uiSystems = new SequentialSystem<GameState>(
   253→            new ButtonInteractionSystem(_world),
   254→            new ButtonMeshPrepSystem(_world)
   255→        );
   256→
   257→        // Transform hierarchy system must run AFTER any systems that modify transforms
   258→        // but BEFORE any systems read world transforms (rendering, etc.)
   259→        var transformHierarchySystem = new TransformHierarchySystem(_world);
   260→
   261→        // Cursor position must update after layout/UI to use current camera state
   262→        var cursorLateUpdateSystem = new CursorPositionSystem(_world, _camera, _viewportManager);
   263→
   264→        return new SequentialSystem<GameState>(
   265→            inputSystems,
   266→            layoutSystems,  // Layout before UI interaction
   267→            uiSystems,
   268→            transformHierarchySystem,         // Propagate transform hierarchy dirty flags
   269→            cursorLateUpdateSystem,           // Cursor position updates after camera
   270→            new CursorDrawPrepSystem(_world)  // Draw prep after position is finalized
   271→        );
   272→    }
   273→
   274→    private SequentialSystem<GameState> CreateDrawSystem()
   275→    {
   276→        var pixelPerfectRendering = SettingsManager.Instance.Settings.PixelPerfectRendering;
   277→
   278→        var prepDrawSystems = new SequentialSystem<GameState>(
   279→            new TextPrepSystem(_world, pixelPerfectRendering)
   280→        );
   281→
   282→        var renderSystem = new MasterRenderSystem(
   283→            _spriteBatch,
   284→            _graphicsDevice,
   285→            _camera,
   286→            _renderTargets,
   287→            _world
   288→        );
   289→
   290→        var finalDrawToScreenSystem = new FinalDrawSystem(_spriteBatch, _graphicsDevice, _viewportManager, _camera, _renderTargets);
   291→
   292→        return new SequentialSystem<GameState>(
   293→            prepDrawSystems,
   294→            renderSystem,
   295→            finalDrawToScreenSystem
   296→        );
   297→    }
   298→
   299→    public void Dispose()
   300→    {
   301→        UpdateSystem.Dispose();
   302→        DrawSystem.Dispose();
   303→        foreach (var rt in _renderTargets.Values)
   304→        {
   305→            rt.Dispose();
   306→        }
   307→        _world.Dispose();
   308→        GC.SuppressFinalize(this);
   309→    }
