using System;
using System.Collections.Generic;
using System.IO;
using System.Text.Json;
using System.Text.Json.Serialization;

namespace MonoDreams.Input;

public class InputReplayPlan
{
    [JsonPropertyName("description")]
    public string Description { get; set; }

    [JsonPropertyName("startLevel")]
    public string StartLevel { get; set; }

    [JsonPropertyName("commands")]
    public List<InputReplayCommand> Commands { get; set; }

    /// <summary>
    /// Reads the startLevel field from input_replay.json without fully parsing the plan.
    /// Returns null if the file doesn't exist or has no startLevel.
    /// </summary>
    public static string TryGetStartLevel(string debugDirectory)
    {
        var filePath = Path.Combine(debugDirectory, "input_replay.json");
        if (!File.Exists(filePath)) return null;

        try
        {
            var json = File.ReadAllText(filePath);
            var plan = JsonSerializer.Deserialize<InputReplayPlan>(json, new JsonSerializerOptions
            {
                PropertyNameCaseInsensitive = true
            });
            return plan?.StartLevel;
        }
        catch (Exception)
        {
            return null;
        }
    }
}

public class InputReplayCommand
{
    [JsonPropertyName("action")]
    public string Action { get; set; }

    [JsonPropertyName("type")]
    public string Type { get; set; }

    [JsonPropertyName("time")]
    public float Time { get; set; }
}

