     1→using System;
     2→using System.IO;
     3→using System.Threading.Tasks;
     4→using DefaultEcs.System;
     5→using Microsoft.Xna.Framework;
     6→using Microsoft.Xna.Framework.Graphics;
     7→using MonoDreams.State;
     8→
     9→namespace MonoDreams.System.Debug;
    10→
    11→public sealed class ScreenshotCaptureSystem : ISystem<GameState>, IDisposable
    12→{
    13→    private readonly GraphicsDevice _graphicsDevice;
    14→    private readonly float _captureIntervalSeconds;
    15→    private readonly string _outputDirectory;
    16→
    17→    private float _timeSinceLastCapture;
    18→    private int _counter;
    19→    private bool _pendingSave;
    20→    private Color[] _pixelBuffer;
    21→    private Texture2D _stagingTexture;
    22→
    23→    public bool IsEnabled { get; set; } = true;
    24→
    25→    public ScreenshotCaptureSystem(GraphicsDevice graphicsDevice, float captureIntervalSeconds, string outputDirectory)
    26→    {
    27→        _graphicsDevice = graphicsDevice;
    28→        _captureIntervalSeconds = captureIntervalSeconds;
    29→        _outputDirectory = outputDirectory;
    30→
    31→        Directory.CreateDirectory(outputDirectory);
    32→        Logger.Info($"ScreenshotCaptureSystem initialized. Interval: {captureIntervalSeconds}s, output: {outputDirectory}");
    33→    }
    34→
    35→    public void Update(GameState state)
    36→    {
    37→        if (!IsEnabled) return;
    38→
    39→        _timeSinceLastCapture += state.Time;
    40→
    41→        if (_timeSinceLastCapture < _captureIntervalSeconds) return;
    42→        if (_pendingSave) return;
    43→
    44→        _timeSinceLastCapture = 0f;
    45→
    46→        var pp = _graphicsDevice.PresentationParameters;
    47→        int width = pp.BackBufferWidth;
    48→        int height = pp.BackBufferHeight;
    49→
    50→        // Lazily allocate or resize buffers
    51→        if (_pixelBuffer == null || _pixelBuffer.Length != width * height)
    52→        {
    53→            _pixelBuffer = new Color[width * height];
    54→            _stagingTexture?.Dispose();
    55→            _stagingTexture = new Texture2D(_graphicsDevice, width, height);
    56→        }
    57→
    58→        // Read backbuffer data (must be on main thread, after FinalDrawSystem)
    59→        _graphicsDevice.GetBackBufferData(_pixelBuffer);
    60→        _stagingTexture.SetData(_pixelBuffer);
    61→
    62→        // Encode to PNG on main thread (SaveAsPng needs the graphics context)
    63→        var pngData = new MemoryStream();
    64→        _stagingTexture.SaveAsPng(pngData, width, height);
    65→        var pngBytes = pngData.ToArray();
    66→        pngData.Dispose();
    67→
    68→        var counter = _counter++;
    69→        var gameTime = state.TotalTime;
    70→        var timestamp = DateTime.Now.ToString("yyyyMMdd_HHmmss_fff");
    71→        var filename = $"screenshot_{counter:D6}_gt{gameTime:F2}_{timestamp}.png";
    72→        var filePath = Path.Combine(_outputDirectory, filename);
    73→
    74→        _pendingSave = true;
    75→        Task.Run(() =>
    76→        {
    77→            try
    78→            {
    79→                File.WriteAllBytes(filePath, pngBytes);
    80→                Logger.Debug($"Screenshot saved: {filename}");
    81→            }
    82→            catch (Exception ex)
    83→            {
    84→                Logger.Error($"Failed to save screenshot: {ex.Message}");
    85→            }
    86→            finally
    87→            {
    88→                _pendingSave = false;
    89→            }
    90→        });
    91→    }
    92→
    93→    public void Dispose()
    94→    {
    95→        _stagingTexture?.Dispose();
    96→        GC.SuppressFinalize(this);
    97→    }
    98→}
    99→

