     9→/// <summary>
    10→/// Holds the state required to render the Dialogue UI.
    11→/// Typically added to a single entity representing the UI.
    12→/// </summary>
    13→public struct DialogueUIStateComponent : IComponent
    14→{
    15→    // --- Control ---
    16→    public bool IsActive; // Is the dialogue UI currently visible?
    17→
    18→    // --- Content ---
    19→    public Texture2D SpeakerEmoteTexture; // Texture for the speaker's portrait/emote. Null if none.
    20→    public string CurrentText;         // The current line of text to display.
    21→    public BitmapFont DialogueFont;      // Font to use for the text.
    22→    public Color TextColor;            // Color of the text.
    23→
    24→    // --- Indicators ---
    25→    public bool ShowNextIndicator; // Show the "continue" prompt graphic?
    26→    public Texture2D NextIndicatorTexture; // Texture for the indicator.
    27→
    28→    // --- Layout & Appearance ---
    29→    public Texture2D DialogueBoxTexture; // The 9-patch texture for the background box.
    30→    public NinePatchInfo DialogueBoxNinePatch; // Definition of the 9-patch slicing for the box.
    31→    public Vector2 DialogueBoxSize; // The target size for the dialogue box on screen.
    32→
    33→    public Vector2 DialogueBoxOffset;  // Position offset for the dialogue box relative to the main UI top-left.
    34→    public Vector2 EmoteOffset;        // Position offset for the emote relative to the main UI top-left.
    35→    public Vector2 TextOffset;         // Position offset for the text block relative to the main UI top-left.
    36→    public Rectangle TextArea;         // Bounding box for text placement relative to the main UI top-left (used for positioning/wrapping).
    37→    public Vector2 NextIndicatorOffset; // Position offset for the indicator relative to the main UI top-left.
    38→
    39→    // Defaults can be set here or when creating the component
    40→    // Example: public Color TextColor = Color.White;
    41→    public void Dispose()
    42→    {
    43→        SpeakerEmoteTexture?.Dispose();
    44→        NextIndicatorTexture?.Dispose();
    45→        DialogueBoxTexture?.Dispose();
    46→        GC.SuppressFinalize(this);
    47→    }
    48→
    49→    public ISite? Site { get; set; }
    50→    public bool IsTextFullyRevealed { get; set; }
    51→    public int TextRevealingSpeed { get; set; }
    52→    public int VisibleCharacterCount { get; set; }
    53→    public float TextRevealStartTime { get; set; }
    54→
    55→    public event EventHandler? Disposed;
    56→}

