   152→    /// <summary>
   153→    /// Calculates and adds the 9 DrawElements needed to render a 9-patch sprite.
   154→    /// Assumes NinePatchInfo contains margin definitions relative to the source texture.
   155→    /// </summary>
   156→    private void AddNinePatchDrawElements(ref DrawComponent drawComp, Texture2D texture, NinePatchInfo ninePatchDef, Vector2 pos, Vector2 size, Color color, float layerDepth, RenderTargetID target)
   157→    {
   158→        // Assuming texture is the full source for the 9-patch
   159→        int texWidth = texture.Width;
   160→        int texHeight = texture.Height;
   161→        
   162→        // Extract margins (assuming they define the stretchable center)
   163→        int marginLeft = 16; // ninePatchDef.MarginLeft;
   164→        int marginTop = 16; // ninePatchDef.MarginTop;
   165→        int marginRight = 16; // ninePatchDef.MarginRight;
   166→        int marginBottom = 28; // ninePatchDef.MarginBottom;
   167→        
   168→        // Calculate dimensions of fixed and stretchable areas from the source texture
   169→        int fixedWidthLeft = marginLeft;
   170→        int fixedWidthRight = marginRight;
   171→        int fixedHeightTop = marginTop;
   172→        int fixedHeightBottom = marginBottom;
   173→        int stretchableWidthSource = texWidth - marginLeft - marginRight;
   174→        int stretchableHeightSource = texHeight - marginTop - marginBottom;
   175→        
   176→        // Calculate dimensions of destination areas
   177→        int fixedWidthDest = fixedWidthLeft + fixedWidthRight;
   178→        int fixedHeightDest = fixedHeightTop + fixedHeightBottom;
   179→        int stretchableWidthDest = Math.Max(0, (int)size.X - fixedWidthDest);
   180→        int stretchableHeightDest = Math.Max(0, (int)size.Y - fixedHeightDest);
   181→        
   182→        // Define Source Rectangles (relative to the texture)
   183→        Rectangle srcTL = new Rectangle(0, 0, fixedWidthLeft, fixedHeightTop);
   184→        Rectangle srcT = new Rectangle(marginLeft, 0, stretchableWidthSource, fixedHeightTop);
   185→        Rectangle srcTR = new Rectangle(texWidth - fixedWidthRight, 0, fixedWidthRight, fixedHeightTop);
   186→        Rectangle srcL = new Rectangle(0, marginTop, fixedWidthLeft, stretchableHeightSource);
   187→        Rectangle srcC = new Rectangle(marginLeft, marginTop, stretchableWidthSource, stretchableHeightSource);
   188→        Rectangle srcR = new Rectangle(texWidth - fixedWidthRight, marginTop, fixedWidthRight, stretchableHeightSource);
   189→        Rectangle srcBL = new Rectangle(0, texHeight - fixedHeightBottom, fixedWidthLeft, fixedHeightBottom);
   190→        Rectangle srcB = new Rectangle(marginLeft, texHeight - fixedHeightBottom, stretchableWidthSource, fixedHeightBottom);
   191→        Rectangle srcBR = new Rectangle(texWidth - fixedWidthRight, texHeight - fixedHeightBottom, fixedWidthRight, fixedHeightBottom);
   192→        
   193→        // Define Destination Positions and Sizes (relative to pos)
   194→        Vector2 posTL = pos;
   195→        Vector2 posT = pos + new Vector2(fixedWidthLeft, 0);
   196→        Vector2 posTR = pos + new Vector2(fixedWidthLeft + stretchableWidthDest, 0);
   197→        Vector2 posL = pos + new Vector2(0, fixedHeightTop);
   198→        Vector2 posC = pos + new Vector2(fixedWidthLeft, fixedHeightTop);
   199→        Vector2 posR = pos + new Vector2(fixedWidthLeft + stretchableWidthDest, fixedHeightTop);
   200→        Vector2 posBL = pos + new Vector2(0, fixedHeightTop + stretchableHeightDest);
   201→        Vector2 posB = pos + new Vector2(fixedWidthLeft, fixedHeightTop + stretchableHeightDest);
   202→        Vector2 posBR = pos + new Vector2(fixedWidthLeft + stretchableWidthDest, fixedHeightTop + stretchableHeightDest);
   203→        
   204→        Vector2 sizeTL = new Vector2(fixedWidthLeft, fixedHeightTop);
   205→        Vector2 sizeT = new Vector2(stretchableWidthDest, fixedHeightTop);
   206→        Vector2 sizeTR = new Vector2(fixedWidthRight, fixedHeightTop);
   207→        Vector2 sizeL = new Vector2(fixedWidthLeft, stretchableHeightDest);
   208→        Vector2 sizeC = new Vector2(stretchableWidthDest, stretchableHeightDest);
   209→        Vector2 sizeR = new Vector2(fixedWidthRight, stretchableHeightDest);
   210→        Vector2 sizeBL = new Vector2(fixedWidthLeft, fixedHeightBottom);
   211→        Vector2 sizeB = new Vector2(stretchableWidthDest, fixedHeightBottom);
   212→        Vector2 sizeBR = new Vector2(fixedWidthRight, fixedHeightBottom);
   213→        
   214→        // Create DrawElements - Ensure sizes are non-negative
   215→        if (sizeTL.X > 0 && sizeTL.Y > 0) AddElement(ref drawComp, texture, target, posTL, sizeTL, srcTL, color, layerDepth); // Top Left
   216→        if (sizeT.X > 0 && sizeT.Y > 0)   AddElement(ref drawComp, texture, target, posT, sizeT, srcT, color, layerDepth);    // Top
   217→        if (sizeTR.X > 0 && sizeTR.Y > 0) AddElement(ref drawComp, texture, target, posTR, sizeTR, srcTR, color, layerDepth); // Top Right
   218→        if (sizeL.X > 0 && sizeL.Y > 0)   AddElement(ref drawComp, texture, target, posL, sizeL, srcL, color, layerDepth);    // Left
   219→        if (sizeC.X > 0 && sizeC.Y > 0)   AddElement(ref drawComp, texture, target, posC, sizeC, srcC, color, layerDepth);    // Center
   220→        if (sizeR.X > 0 && sizeR.Y > 0)   AddElement(ref drawComp, texture, target, posR, sizeR, srcR, color, layerDepth);    // Right
   221→        if (sizeBL.X > 0 && sizeBL.Y > 0) AddElement(ref drawComp, texture, target, posBL, sizeBL, srcBL, color, layerDepth); // Bottom Left
   222→        if (sizeB.X > 0 && sizeB.Y > 0)   AddElement(ref drawComp, texture, target, posB, sizeB, srcB, color, layerDepth);    // Bottom
   223→        if (sizeBR.X > 0 && sizeBR.Y > 0) AddElement(ref drawComp, texture, target, posBR, sizeBR, srcBR, color, layerDepth); // Bottom Right
   224→    }
   225→
   226→    // Small helper to reduce repetition when adding elements
   227→    private void AddElement(ref DrawComponent drawComp, Texture2D texture, RenderTargetID target, Vector2 pos, Vector2 size, Rectangle srcRect, Color color, float layerDepth)
