     6→namespace MonoDreams.Examples.System.Dialogue // Or your appropriate systems namespace
     7→{
     8→    /// <summary>
     9→    /// Updates the dynamic state of the Dialogue UI, such as text reveal progress
    10→    /// and indicator visibility.
    11→    /// </summary>
    12→    [With(typeof(DialogueUIStateComponent))] // Query for entities with the state component
    13→    public sealed class DialogueUpdateSystem : AEntitySetSystem<GameState>
    14→    {
    15→        public DialogueUpdateSystem(World world)
    16→        : base(world) // Run sequentially by default
    17→        { }
    18→
    19→        protected override void Update(GameState state, in Entity entity)
    20→        {
    21→            // Get component by reference to modify it
    22→            ref var uiState = ref entity.Get<DialogueUIStateComponent>();
    23→
    24→            // Only update if the UI is active
    25→            if (!uiState.IsActive)
    26→            {
    27→                // Ensure reveal state is reset if UI becomes inactive? Optional.
    28→                // uiState.IsRevealed = false;
    29→                // uiState.VisibleCharacterCount = 0;
    30→                return;
    31→            }
    32→
    33→            // --- Update Text Reveal State ---
    34→            int maxChars = uiState.CurrentText?.Length ?? 0;
    35→
    36→            // Check if already revealed or instant reveal speed
    37→            if (uiState.IsTextFullyRevealed || uiState.TextRevealingSpeed <= 0)
    38→            {
    39→                uiState.VisibleCharacterCount = maxChars;
    40→                uiState.IsTextFullyRevealed = true; // Ensure flag is set
    41→            }
    42→            else
    43→            {
    44→                // Initialize start time if it's marked as invalid (e.g., after text change)
    45→                if (float.IsNaN(uiState.TextRevealStartTime))
    46→                {
    47→                    uiState.TextRevealStartTime = state.TotalTime; // Assuming GameState has TotalTime in seconds
    48→                }
    49→
    50→                float elapsedTime = state.TotalTime - uiState.TextRevealStartTime;
    51→                int targetVisibleChars = (int)Math.Floor(elapsedTime * uiState.TextRevealingSpeed);
    52→                uiState.VisibleCharacterCount = Math.Max(0, Math.Min(targetVisibleChars, maxChars));
    53→                uiState.IsTextFullyRevealed = (uiState.VisibleCharacterCount >= maxChars);
    54→            }
    55→
    56→            // --- Update Next Indicator Visibility ---
    57→            // Basic logic: Show indicator when text is fully revealed.
    58→            // TODO: Refine this logic later based on dialogue runner state (e.g., waiting for input).
    59→            uiState.ShowNextIndicator = uiState.IsTextFullyRevealed;
    60→
    61→            // --- Update Indicator Animation State (Future Task) ---
    62→            // if (uiState.ShowNextIndicator) { /* Update bobble offset based on state.TotalTime */ }
    63→        }
    64→    }
    65→}

