    11→public class MovementSystem(World world, IParallelRunner parallelRunner)
    12→    : AEntitySetSystem<GameState>(world.GetEntities().With<Transform>().With<InputControlled>().AsSet(), parallelRunner)
    13→{
    14→    protected override void Update(GameState state, in Entity entity)
    15→    {
    16→        ref var transform = ref entity.Get<Transform>();
    17→
    18→        if (InputState.Left.Pressed(state))
    19→            transform.TranslateX(-Constants.MaxWalkVelocity * state.Time);
    20→        if (InputState.Right.Pressed(state))
    21→            transform.TranslateX(Constants.MaxWalkVelocity * state.Time);
    22→        if (InputState.Up.Pressed(state))
    23→            transform.TranslateY(-Constants.MaxWalkVelocity * state.Time);
    24→        if (InputState.Down.Pressed(state))
    25→            transform.TranslateY(Constants.MaxWalkVelocity * state.Time);
    26→    }
    27→}

