     8→// This system ONLY updates the state of the text, doesn't prepare drawing
     9→[With(typeof(DynamicText))] // Ensures entities have DynamicText
    10→public sealed class TextUpdateSystem : AEntitySetSystem<GameState>
    11→{
    12→    public TextUpdateSystem(World world) : base(world) { }
    13→
    14→    protected override void Update(GameState state, in Entity entity)
    15→    {
    16→        ref var text = ref entity.Get<DynamicText>();
    17→
    18→        if (text.IsRevealed || text.RevealingSpeed <= 0)
    19→        {
    20→            text.VisibleCharacterCount = text.TextContent?.Length ?? 0;
    21→            text.IsRevealed = true; // Ensure flag is set
    22→            return; // No update needed if already revealed or instant reveal
    23→        }
    24→
    25→        // Initialize start time if not set
    26→        if (float.IsNaN(text.RevealStartTime))
    27→        {
    28→            text.RevealStartTime = state.TotalTime; // Assuming GameState has TotalTime in seconds
    29→        }
    30→
    31→        float elapsedTime = state.TotalTime - text.RevealStartTime;
    32→        int targetVisibleChars = (int)Math.Floor(elapsedTime * text.RevealingSpeed);
    33→        int maxChars = text.TextContent?.Length ?? 0;
    34→
    35→        text.VisibleCharacterCount = Math.Max(0, Math.Min(targetVisibleChars, maxChars));
    36→
    37→        if (text.VisibleCharacterCount >= maxChars)
    38→        {
    39→            text.IsRevealed = true;
    40→        }
    41→    }
    42→}

