    18→        Texture2D topLeft, Texture2D top, Texture2D topRight, Texture2D left, Texture2D center,
    19→        Texture2D right, Texture2D bottomLeft, Texture2D bottom, Texture2D bottomRight) NinePatchTextures;
    20→    public readonly (
    21→        Rectangle topLeft, Rectangle top, Rectangle topRight, Rectangle left, Rectangle center,
    22→        Rectangle right, Rectangle bottomLeft, Rectangle bottom, Rectangle bottomRight) NinePatchDestinations;
    23→
    24→    public DrawInfo(
    25→        RenderTarget2D renderTarget, Texture2D spriteSheet, Point? size = null, Rectangle? source = null,
    26→        Color? color = null, Enum layer = null, NinePatchInfo? ninePatchInfo = null,
    27→        GraphicsDevice? graphicsDevice = null)
    28→    {
