    32→        Source = source ?? spriteSheet.Bounds;
    33→        Color = color ?? Color.White;
    34→        Layer = layer;
    35→        NinePatchInfo = ninePatchInfo;
    36→
    37→        if (ninePatchInfo == null || graphicsDevice == null)
    38→        {
    39→            NinePatchTextures = (null, null, null, null, null, null, null, null, null);
    40→            NinePatchDestinations = (Rectangle.Empty, Rectangle.Empty, Rectangle.Empty, Rectangle.Empty, Rectangle.Empty, Rectangle.Empty, Rectangle.Empty, Rectangle.Empty, Rectangle.Empty);
    41→        }
    42→        else
    43→        {
    44→            NinePatchTextures = (
    45→                spriteSheet.Crop(ninePatchInfo.Value.TopLeft, graphicsDevice),
    46→                spriteSheet.Crop(ninePatchInfo.Value.Top, graphicsDevice),
    47→                spriteSheet.Crop(ninePatchInfo.Value.TopRight, graphicsDevice),
    48→                spriteSheet.Crop(ninePatchInfo.Value.Left, graphicsDevice),
    49→                spriteSheet.Crop(ninePatchInfo.Value.Center, graphicsDevice),
