    53→                spriteSheet.Crop(ninePatchInfo.Value.BottomRight, graphicsDevice)
    54→            );
    55→
    56→            var borderSize = ninePatchInfo.Value.BorderSize;
    57→            var sizeValue = Size;
    58→
    59→            NinePatchDestinations = (
    60→                new Rectangle(0, 0, borderSize, borderSize),
    61→                new Rectangle(borderSize, 0, sizeValue.X - 2 * borderSize, borderSize),
    62→                new Rectangle(sizeValue.X - borderSize, 0, borderSize, borderSize),
