    10→namespace MonoDreams.Examples.System;
    11→
    12→public class DialogueTriggerSystem : AEntitySetSystem<GameState>
    13→{
    14→    private World _world;
    15→    private readonly Entity _player;
    16→    
    17→    public DialogueTriggerSystem(World world, Entity player)
    18→        : base(world.GetEntities().With<DialogueTrigger>().With<Transform>().AsSet())
    19→    {
    20→        _world = world;
    21→        _player = player;
    22→    }
    23→
    24→    protected override void Update(GameState state, in Entity entity)
    25→    {
    26→        var trigger = entity.Get<DialogueTrigger>();
    27→
    28→        // Skip if the trigger is one-time-only and already used
    29→        if (trigger.OneTimeOnly && trigger.HasBeenUsed) return;
    30→
    31→        // Check if trigger conditions are met
    32→        bool shouldTrigger = false;
    33→
    34→        switch (trigger.Type)
    35→        {
    36→            case TriggerType.Proximity:
    37→                // Check if player is within proximity radius
    38→                if (entity.Has<Transform>() && _player.Has<Transform>())
    39→                {
    40→                    var triggerPos = entity.Get<Transform>().Position;
    41→                    var playerPos = _player.Get<Transform>().Position;
    42→
    43→                    float distance = Vector2.Distance(triggerPos, playerPos);
    44→                    shouldTrigger = distance <= trigger.ProximityRadius;
    45→                }
    46→                break;
    47→
    48→            case TriggerType.Interaction:
    49→                // Check if player is close and interaction key is pressed
    50→                if (entity.Has<Transform>() && _player.Has<Transform>())
    51→                {
    52→                    var triggerPos = entity.Get<Transform>().Position;
    53→                    var playerPos = _player.Get<Transform>().Position;
    54→
    55→                    float distance = Vector2.Distance(triggerPos, playerPos);
    56→                    shouldTrigger = distance <= trigger.ProximityRadius &&
    57→                                    Keyboard.GetState().IsKeyDown(trigger.InteractionKey);
    58→                }
    59→                break;
    60→                
    61→            case TriggerType.Automatic:
    62→                // Always trigger
    63→                shouldTrigger = true;
    64→                break;
    65→        }
    66→        
    67→        if (shouldTrigger)
    68→        {
    69→            // Mark trigger as used if one-time-only
    70→            if (trigger.OneTimeOnly)
    71→            {
    72→                trigger.HasBeenUsed = true;
    73→            }
    74→            
    75→            // Create or get dialogue entity
    76→            Entity dialogueEntity;
    77→            if (entity.Has<DialogueComponent>())
    78→            {
    79→                dialogueEntity = entity;
    80→            }
    81→            else
    82→            {
    83→                // Create a new dialogue entity
    84→                dialogueEntity = _world.CreateEntity();
    85→                // dialogueEntity.Set(new DialogueComponent());
    86→                // dialogueEntity.Set(new DialogueState());
    87→            }
    88→            
    89→            // Publish start message
    90→            _world.Publish(new DialogueStartMessage(
    91→                dialogueEntity,
    92→                trigger.StartNode
    93→            ));
    94→        }
    95→    }
    96→}
    97→

