    27→namespace MonoDreams.Examples.Screens;
    28→
    29→public class LoadLevelExampleGameScreen : IGameScreen
    30→{
    31→    private readonly ContentManager _content;
    32→    private readonly Game _game;
    33→    private readonly GraphicsDevice _graphicsDevice;
    34→    private readonly Camera _camera;
    35→    private readonly ViewportManager _viewportManager;
    36→    private readonly DefaultParallelRunner _parallelRunner;
    37→    private readonly SpriteBatch _spriteBatch;
    38→    private readonly World _world;
    39→    private readonly LevelLoader _levelLoader;
    40→    private readonly Dictionary<RenderTargetID, RenderTarget2D> _renderTargets;
    41→    
    42→    public LoadLevelExampleGameScreen(Game game, GraphicsDevice graphicsDevice, ContentManager content, Camera camera,
    43→        ViewportManager viewportManager, DefaultParallelRunner parallelRunner, SpriteBatch spriteBatch)
    44→    {
    45→        _game = game;
    46→        _graphicsDevice = graphicsDevice;
    47→        _content = content;
    48→        _camera = camera;
    49→        _viewportManager = viewportManager;
    50→        _parallelRunner = parallelRunner;
    51→        _spriteBatch = spriteBatch;
    52→        _renderTargets = new Dictionary<RenderTargetID, RenderTarget2D>
    53→        {
    54→            { RenderTargetID.Main, new RenderTarget2D(graphicsDevice, _viewportManager.VirtualWidth, _viewportManager.VirtualHeight) },
    55→            { RenderTargetID.UI, new RenderTarget2D(graphicsDevice, _viewportManager.VirtualWidth, _viewportManager.VirtualHeight) },
    56→            { RenderTargetID.HUD, new RenderTarget2D(graphicsDevice, _viewportManager.VirtualWidth, _viewportManager.VirtualHeight) }
    57→        };
    58→        
    59→        camera.Position = new Vector2(0, 0);
    60→        
    61→        _world = new World();
    62→        // _levelLoader = new LevelLoader(_world, graphicsDevice, _content, _spriteBatch, _renderTargets);
    63→        UpdateSystem = CreateUpdateSystem();
    64→        DrawSystem = CreateDrawSystem();
    65→    }
    66→
    67→    public ISystem<GameState> UpdateSystem { get; }
    68→    public ISystem<GameState> DrawSystem { get; }
    69→    public void Load(ScreenController screenController, ContentManager content)
    70→    {
    71→        var cursorTextures = new Dictionary<CursorType, Texture2D>
    72→        {
    73→            [CursorType.Default] = content.Load<Texture2D>("Mouse sprites/Triangle Mouse icon 1"),
    74→            [CursorType.Pointer] = content.Load<Texture2D>("Mouse sprites/Triangle Mouse icon 2"),
    75→            [CursorType.Hand] = content.Load<Texture2D>("Mouse sprites/Catpaw Mouse icon"),
    76→            // Add more cursor types as needed
    77→        };
    78→
    79→        // Create cursor entity
    80→        Objects.Cursor.Create(_world, cursorTextures, RenderTargetID.HUD);
    81→
    82→        // Check if a level was requested from the level selection screen
    83→        var requestedLevel = screenController.Game.Services.GetService(typeof(RequestedLevelComponent)) as RequestedLevelComponent;
    84→        if (requestedLevel != null)
    85→        {
    86→            // Load the requested level
    87→            _world.Publish(new LoadLevelRequest(requestedLevel.LevelIdentifier));
    88→
    89→            // Remove the service so it doesn't interfere with future screen loads
    90→            screenController.Game.Services.RemoveService(typeof(RequestedLevelComponent));
    91→        }
    92→    }
    93→    
    94→    private SequentialSystem<GameState> CreateUpdateSystem()
    95→    {
    96→        var inputSystems = new ParallelSystem<GameState>(_parallelRunner,
    97→            new CursorInputSystem(_world),
    98→            new InputMappingSystem(_world)
    99→        );
   100→        
   101→        var levelLoadSystems = new SequentialSystem<GameState>(
   102→            new LevelLoadRequestSystem(_world, _content),
   103→            new BlenderLevelParserSystem(_world, _content, _camera),
   104→            new LDtkTileParserSystem(_world, _content),
   105→            new LDtkEntityParserSystem(_world),
   106→            new EntitySpawnSystem(_world, _content, _renderTargets));
   107→        
   108→        // Collision pipeline must run sequentially (movement → velocity → detect → resolve → commit)
   109→        // Individual systems keep their internal _parallelRunner for entity-level parallelism
   110→        var logicSystems = new SequentialSystem<GameState>(
   111→            new MovementSystem(_world, _parallelRunner),
