     1→using DefaultEcs;
     2→using Microsoft.Xna.Framework;
     3→using MonoDreams.Examples.Component;
     4→using MonoDreams.Message;
     5→
     6→namespace MonoDreams.Examples.Message;
     7→
     8→public readonly record struct CollisionMessage(
     9→    Entity BaseEntity,
    10→    Entity CollidingEntity,
    11→    Vector2 ContactPoint,
    12→    Vector2 ContactNormal,
    13→    float ContactTime,
    14→    int Layer = -1,
    15→    CollisionType Type = CollisionType.Generic) : ICollisionMessage
    16→{
    17→    public static CollisionMessage Create(
    18→        Entity entity,
    19→        Entity target,
    20→        Vector2 contactPoint,
    21→        Vector2 contactNormal,
    22→        float contactTime,
    23→        int layer)
    24→    {
    25→        CollisionType collisionType = DetermineCollisionType(entity, target);
    26→        Console.WriteLine($"Collision detected: {entity} vs {target} at {contactPoint} (contact normal: {contactNormal}| time: {contactTime})");
    27→        return new CollisionMessage(entity, target, contactPoint, contactNormal, contactTime, layer, collisionType);
    28→    }
    29→
    30→    private static CollisionType DetermineCollisionType(Entity entity, Entity target)
    31→    {
    32→        var entityType = entity.Get<EntityInfo>().Type;
