   207→        SetDirty();
   208→        return this;
   209→    }
   210→
   211→    /// <summary>Sets the Y component of position.</summary>
   212→    public Transform SetPositionY(float y)
   213→    {
   214→        _position.Y = y;
   215→        SetDirty();
   216→        return this;
   217→    }
   218→
   219→    /// <summary>Adds a delta to position.</summary>
   220→    public Transform Translate(Vector2 delta)
   221→    {
   222→        _position += delta;
