   238→    {
   239→        _position.X += delta;
   240→        SetDirty();
   241→        return this;
   242→    }
   243→
   244→    /// <summary>Adds a delta to the Y component of position.</summary>
   245→    public Transform TranslateY(float delta)
   246→    {
   247→        _position.Y += delta;
   248→        SetDirty();
   249→        return this;
   250→    }
   251→
   252→    #endregion
   253→
   254→    #region Fluent Rotation Methods
   255→
