    10→
    11→[With(typeof(DynamicText), typeof(Transform))] // Ensures entities have these + DrawComponent (from base)
    12→public sealed class TextPrepSystem(World world, bool pixelPerfectRendering) : AEntitySetSystem<GameState>(world)
    13→{
    14→    private readonly bool _pixelPerfectRendering = pixelPerfectRendering;
    15→    // Set useParallel = true if desired and safe
    16→
    17→     protected override void Update(GameState state, in Entity entity)
    18→     {
    19→        ref readonly var transform = ref entity.Get<Transform>();
    20→        ref readonly var text = ref entity.Get<DynamicText>(); // State updated by TextUpdateSystem
    21→
    22→        // *** Strategy: Clear drawables added by this system type ***
    23→         // If SpritePrepSystem already cleared, this might not be needed,
    24→         // BUT if they run in parallel or if clearing isn't global, it's safer.
    25→         // Alternatively, tag DrawElements with their source system/type.
    26→         // Let's assume SpritePrepSystem cleared, and this just adds. If running parallel, MUST clear carefully.
    27→         // drawComponent.Drawables.Clear(); // Be careful with clearing strategy
    28→
    29→        if (text.VisibleCharacterCount <= 0 || string.IsNullOrEmpty(text.TextContent))
    30→        {
    31→            return; // Nothing to draw
    32→        }
    33→
    34→        var layerDepth = text.LayerDepth;
    35→        var visibleChars = Math.Min(text.VisibleCharacterCount, text.TextContent.Length);
    36→        var visibleText = text.TextContent[..visibleChars];
    37→
    38→        // Add a single DrawElement for the visible text (SpriteFont handles glyphs)
    39→        var position = transform.WorldPosition;
    40→        entity.Set(new DrawComponent
    41→        {
    42→             Type = DrawElementType.Text,
    43→             Target = text.Target,
    44→             Text = visibleText,
    45→             Font = text.Font,
    46→             Position = _pixelPerfectRendering
    47→                 ? new Vector2(MathF.Round(position.X), MathF.Round(position.Y))
    48→                 : position,
    49→             Rotation = transform.WorldRotation,
    50→             Color = text.Color,
    51→             LayerDepth = layerDepth,
    52→             Size = text.Font.MeasureString(visibleText), // Store measured size if needed elsewhere
    53→             Scale = transform.WorldScale * new Vector2(text.Scale > 0 ? text.Scale : 0.5f) // Combine world scale with DynamicText scale
    54→             // Add Origin(for alignment), Effects if needed
    55→        });
    56→
    57→         // If using a Bitmap Font/Glyph Atlas:
    58→         // Instead of adding one DrawElement, you would iterate through the
    59→         // 'text.CalculatedGlyphs' (up to VisibleGlyphCount), which should contain
    60→         // position, source rect, texture, etc. for each glyph, and add
    61→         // a DrawElement of Type = Sprite for each one.
    62→     }
    63→}

