     1→using DefaultEcs;
     2→using DefaultEcs.System;
     3→using Microsoft.Xna.Framework;
     4→using Microsoft.Xna.Framework.Graphics;
     5→using MonoDreams.Component;
     6→using MonoDreams.Examples.Component.Draw;
     7→using MonoDreams.State;
     8→using MonoGame.Extended.BitmapFonts;
     9→
    10→namespace MonoDreams.Examples.System.Draw;
    11→
    12→/// <summary>
    13→/// Unified rendering system that handles all draw types: sprites, text, nine-patch, and mesh.
    14→/// This system is game-agnostic - it only renders what's in DrawComponent without any
    15→/// specialized component handling.
    16→/// </summary>
    17→public class MasterRenderSystem(
    18→    SpriteBatch spriteBatch,
    19→    GraphicsDevice graphicsDevice,
    20→    MonoDreams.Component.Camera camera,
    21→    IReadOnlyDictionary<RenderTargetID, RenderTarget2D> renderTargets,
    22→    World world) : ISystem<GameState>
    23→{
    24→    private BasicEffect? _basicEffect;
    25→
    26→    private enum BatchType { None, Sprite, Mesh }
    27→
    28→    private static BatchType GetBatchType(DrawElementType type) =>
    29→        type == DrawElementType.Mesh ? BatchType.Mesh : BatchType.Sprite;
    30→
    31→    private void EnsureBasicEffect()
    32→    {
    33→        _basicEffect ??= new BasicEffect(graphicsDevice)
    34→        {
    35→            VertexColorEnabled = true,
    36→            View = Matrix.Identity,
    37→            Projection = Matrix.CreateOrthographicOffCenter(0, camera.VirtualWidth, camera.VirtualHeight, 0, 0, 1)
    38→        };
    39→    }
    40→
    41→    private void BeginSpriteBatch(Matrix transformMatrix)
    42→    {
    43→        spriteBatch.Begin(
    44→            sortMode: SpriteSortMode.Deferred, // We pre-sort, so no need for FrontToBack
    45→            blendState: BlendState.AlphaBlend,
    46→            samplerState: SamplerState.LinearClamp,
    47→            depthStencilState: DepthStencilState.None,
    48→            rasterizerState: RasterizerState.CullNone,
    49→            effect: null,
    50→            transformMatrix: transformMatrix);
    51→    }
    52→
    53→    private void EndSpriteBatch() => spriteBatch.End();
    54→
    55→    private void ResetGraphicsStateForMeshRendering()
    56→    {
    57→        // Reset states after SpriteBatch.End() before mesh rendering
    58→        graphicsDevice.BlendState = BlendState.AlphaBlend;
    59→        graphicsDevice.DepthStencilState = DepthStencilState.None;
    60→        graphicsDevice.RasterizerState = RasterizerState.CullNone;
    61→        graphicsDevice.SamplerStates[0] = SamplerState.PointClamp;
    62→    }
    63→
    64→    public void Update(GameState state)
    65→    {
    66→        EnsureBasicEffect();
    67→
    68→        foreach (var renderTarget in renderTargets)
    69→        {
    70→            var queryBuilder = world.GetEntities()
    71→                .With((in DrawComponent e) => e.Target == renderTarget.Key);
    72→
    73→            // Main target: respect CullingSystem visibility. HUD/UI: always render.
    74→            if (renderTarget.Key == RenderTargetID.Main)
    75→                queryBuilder.With<Visible>();
    76→
    77→            var drawList = queryBuilder.AsSet();
    78→
    79→            graphicsDevice.SetRenderTarget(renderTarget.Value);
    80→            graphicsDevice.Clear(Color.Transparent);
    81→
    82→            var transformMatrix = Matrix.Identity;
    83→            if (renderTarget.Key == RenderTargetID.Main)
    84→            {
    85→                transformMatrix = camera.GetViewTransformationMatrix();
    86→            }
    87→
    88→            // Sort ALL entities by LayerDepth (stable sort preserves order for same depth)
    89→            var entities = drawList.GetEntities().ToArray();
    90→            var sortedEntities = entities
    91→                .Select((entity, index) => (entity, index, dc: entity.Get<DrawComponent>()))
    92→                .Where(x => x.dc.Type != DrawElementType.Mesh || x.dc.HasValidMesh)
    93→                .OrderBy(x => x.dc.LayerDepth)
    94→                .ThenBy(x => x.index) // Stable sort
    95→                .ToList();
    96→
    97→            if (sortedEntities.Count > 0)
    98→            {
    99→                RenderInterleaved(sortedEntities, transformMatrix);
   100→            }
   101→        }
   102→    }
   103→
   104→    private void RenderInterleaved(
   105→        List<(Entity entity, int index, DrawComponent dc)> sortedEntities,
   106→        Matrix transformMatrix)
   107→    {
   108→        BatchType currentBatch = BatchType.None;
   109→
   110→        foreach (var (_, _, dc) in sortedEntities)
   111→        {
   112→            var requiredBatch = GetBatchType(dc.Type);
   113→
   114→            // Batch type changed - switch context
   115→            if (requiredBatch != currentBatch)
   116→            {
   117→                // End previous batch
   118→                if (currentBatch == BatchType.Sprite)
   119→                    EndSpriteBatch();
   120→
   121→                // Start new batch
   122→                if (requiredBatch == BatchType.Sprite)
   123→                    BeginSpriteBatch(transformMatrix);
   124→                else if (requiredBatch == BatchType.Mesh)
   125→                    ResetGraphicsStateForMeshRendering();
   126→
   127→                currentBatch = requiredBatch;
   128→            }
   129→
   130→            // Draw the element
   131→            if (requiredBatch == BatchType.Sprite)
   132→                DrawElement(dc);
   133→            else
   134→                DrawSingleMesh(dc, transformMatrix);
   135→        }
   136→
   137→        // End final batch if sprites
   138→        if (currentBatch == BatchType.Sprite)
   139→            EndSpriteBatch();
   140→    }
   141→
   142→    private void DrawSingleMesh(DrawComponent dc, Matrix transformMatrix)
   143→    {
   144→        if (!dc.HasValidMesh) return;
   145→
   146→        _basicEffect!.Projection = Matrix.CreateOrthographicOffCenter(
   147→            0, camera.VirtualWidth, camera.VirtualHeight, 0, 0, 1);
   148→        _basicEffect.World = (dc.WorldMatrix ?? Matrix.Identity) * transformMatrix;
   149→
   150→        foreach (var pass in _basicEffect.CurrentTechnique.Passes)
   151→        {
   152→            pass.Apply();
   153→            graphicsDevice.DrawUserIndexedPrimitives(
   154→                dc.PrimitiveType,
   155→                dc.Vertices,
   156→                0,
   157→                dc.Vertices!.Length,
   158→                dc.Indices,
   159→                0,
   160→                dc.GetPrimitiveCount());
   161→        }
   162→    }
   163→
   164→    private void DrawElement(DrawComponent element)
   165→    {
   166→        switch (element.Type)
   167→        {
   168→            case DrawElementType.Sprite:
   169→                if (element.Texture == null) return;
   170→
   171→                // Calculate scale from source to destination size for sub-pixel precision
   172→                Vector2 scale;
   173→                if (element.SourceRectangle.HasValue && element.SourceRectangle.Value.Width > 0 && element.SourceRectangle.Value.Height > 0)
   174→                {
   175→                    scale = new Vector2(
   176→                        element.Size.X / element.SourceRectangle.Value.Width,
   177→                        element.Size.Y / element.SourceRectangle.Value.Height);
   178→                }
   179→                else
   180→                {
   181→                    scale = element.Scale;
   182→                }
   183→
   184→                spriteBatch.Draw(
   185→                    element.Texture,
   186→                    element.Position,  // Vector2 preserves sub-pixel precision
   187→                    element.SourceRectangle,
   188→                    element.Color,
   189→                    element.Rotation,
   190→                    element.Origin,
   191→                    scale,
   192→                    SpriteEffects.None,
   193→                    element.LayerDepth);
   194→                break;
   195→
   196→            case DrawElementType.Text:
   197→                if (element.Font == null || element.Text == null) return;
   198→
   199→                spriteBatch.DrawString(
   200→                    element.Font,
   201→                    element.Text,
   202→                    element.Position,
   203→                    element.Color,
   204→                    element.Rotation,
   205→                    element.Origin,
   206→                    element.Scale,
   207→                    SpriteEffects.None,
   208→                    element.LayerDepth);
   209→                break;
   210→
   211→            case DrawElementType.NinePatch:
   212→                // NinePatch handled by SpritePrepSystem creating 9 sprite elements
   213→                break;
   214→
   215→            case DrawElementType.Mesh:
   216→                // Handled separately in DrawSingleMesh via RenderInterleaved
   217→                break;
   218→        }
   219→    }
   220→
   221→    public void Dispose()
   222→    {
   223→        _basicEffect?.Dispose();
   224→    }
   225→
   226→    public bool IsEnabled { get; set; } = true;
   227→}

