    11→// Prepares DrawElements for static sprites and 9-patches
    12→[With(typeof(DrawComponent), typeof(SpriteInfo), typeof(Transform), typeof(Visible))]
    13→public class SpritePrepSystem(World world, GraphicsDevice graphicsDevice, bool pixelPerfectRendering) : AEntitySetSystem<GameState>(world)
    14→{
    15→    private readonly bool _pixelPerfectRendering = pixelPerfectRendering;
    16→    private readonly Dictionary<string, Texture2D> _ninePatchTextureCache = new();
    17→
    18→    protected override void Update(GameState state, in Entity entity)
    19→    {
    20→        ref readonly var transform = ref entity.Get<Transform>();
    21→        ref readonly var spriteInfo = ref entity.Get<SpriteInfo>();
    22→        ref readonly var drawComponent = ref entity.Get<DrawComponent>();
    23→
    24→        if (spriteInfo.NinePatchData.HasValue && spriteInfo.SpriteSheet != null)
    25→        {
    26→            // Create a runtime nine-patch texture
    27→            var ninePatchTexture = CreateNinePatchTexture(spriteInfo);
    28→
    29→            var position = transform.WorldPosition + spriteInfo.Offset;
    30→            drawComponent.Texture = ninePatchTexture;
    31→            drawComponent.Position = _pixelPerfectRendering
    32→                ? new Vector2(MathF.Round(position.X), MathF.Round(position.Y))
    33→                : position;
    34→            drawComponent.Rotation = transform.WorldRotation;
    35→            drawComponent.Scale = transform.WorldScale;
    36→            drawComponent.Size = spriteInfo.Size;
    37→            drawComponent.SourceRectangle = null;
    38→            drawComponent.Color = spriteInfo.Color;
    39→            drawComponent.LayerDepth = spriteInfo.LayerDepth;
    40→            drawComponent.Origin = spriteInfo.Origin;
    41→        }
    42→        // --- Handle Regular Sprite ---
    43→        else if (spriteInfo.SpriteSheet != null)
    44→        {
    45→            var position = transform.WorldPosition + spriteInfo.Offset;
    46→            drawComponent.Texture = spriteInfo.SpriteSheet;
    47→            drawComponent.Position = _pixelPerfectRendering
    48→                ? new Vector2(MathF.Round(position.X), MathF.Round(position.Y))
    49→                : position;
    50→            drawComponent.Rotation = transform.WorldRotation;
    51→            drawComponent.Scale = transform.WorldScale;
    52→            drawComponent.Size = spriteInfo.Size;
    53→            drawComponent.SourceRectangle = spriteInfo.Source;
    54→            drawComponent.Color = spriteInfo.Color;
    55→            drawComponent.LayerDepth = spriteInfo.LayerDepth;
    56→            drawComponent.Origin = spriteInfo.Origin;
    57→        }
    58→    }
    59→
    60→    private Texture2D CreateNinePatchTexture(SpriteInfo spriteInfo)
    61→    {
    62→        var ninePatch = spriteInfo.NinePatchData.Value;
    63→        var targetSize = spriteInfo.Size;
    64→        
    65→        // Create a cache key based on source texture, nine-patch data, and target size
    66→        var cacheKey = $"{spriteInfo.SpriteSheet.GetHashCode()}_{ninePatch.GetHashCode()}_{targetSize.X}x{targetSize.Y}";
    67→        
    68→        if (_ninePatchTextureCache.TryGetValue(cacheKey, out var cachedTexture))
    69→        {
    70→            return cachedTexture;
    71→        }
    72→
    73→        // Create new render target with the target size
    74→        var renderTarget = new RenderTarget2D(graphicsDevice, 
    75→            (int)targetSize.X, (int)targetSize.Y, 
    76→            false, SurfaceFormat.Color, DepthFormat.None);
    77→
    78→        // Create a temporary SpriteBatch for rendering to the render target
    79→        using var spriteBatch = new SpriteBatch(graphicsDevice);
    80→        
    81→        // Set render target and clear
    82→        graphicsDevice.SetRenderTarget(renderTarget);
    83→        graphicsDevice.Clear(Color.Transparent);
    84→
    85→        spriteBatch.Begin(SpriteSortMode.Immediate, BlendState.AlphaBlend, 
    86→            SamplerState.PointClamp, DepthStencilState.None, RasterizerState.CullNone);
    87→
    88→        // Calculate dimensions for stretching
    89→        var leftWidth = ninePatch.TopLeft.Width;
    90→        var rightWidth = ninePatch.TopRight.Width;
    91→        var topHeight = ninePatch.TopLeft.Height;
    92→        var bottomHeight = ninePatch.BottomLeft.Height;
    93→        
    94→        var centerWidth = targetSize.X - leftWidth - rightWidth;
    95→        var centerHeight = targetSize.Y - topHeight - bottomHeight;
    96→
    97→        // Draw the 9 patches
    98→        // Top-left corner
    99→        spriteBatch.Draw(spriteInfo.SpriteSheet, 
   100→            new Rectangle(0, 0, leftWidth, topHeight),
   101→            ninePatch.TopLeft, Color.White);
   102→
   103→        // Top edge (stretched horizontally)
   104→        spriteBatch.Draw(spriteInfo.SpriteSheet,
   105→            new Rectangle(leftWidth, 0, (int)centerWidth, topHeight),
   106→            ninePatch.Top, Color.White);
   107→
   108→        // Top-right corner
   109→        spriteBatch.Draw(spriteInfo.SpriteSheet,
   110→            new Rectangle((int)(targetSize.X - rightWidth), 0, rightWidth, topHeight),
   111→            ninePatch.TopRight, Color.White);
   112→
   113→        // Left edge (stretched vertically)
   114→        spriteBatch.Draw(spriteInfo.SpriteSheet,
   115→            new Rectangle(0, topHeight, leftWidth, (int)centerHeight),
   116→            ninePatch.Left, Color.White);
   117→
   118→        // Center (stretched both ways)
   119→        spriteBatch.Draw(spriteInfo.SpriteSheet,
   120→            new Rectangle(leftWidth, topHeight, (int)centerWidth, (int)centerHeight),
   121→            ninePatch.Center, Color.White);
   122→
   123→        // Right edge (stretched vertically)
   124→        spriteBatch.Draw(spriteInfo.SpriteSheet,
   125→            new Rectangle((int)(targetSize.X - rightWidth), topHeight, rightWidth, (int)centerHeight),
   126→            ninePatch.Right, Color.White);
   127→
   128→        // Bottom-left corner
   129→        spriteBatch.Draw(spriteInfo.SpriteSheet,
   130→            new Rectangle(0, (int)(targetSize.Y - bottomHeight), leftWidth, bottomHeight),
   131→            ninePatch.BottomLeft, Color.White);
   132→
   133→        // Bottom edge (stretched horizontally)
   134→        spriteBatch.Draw(spriteInfo.SpriteSheet,
   135→            new Rectangle(leftWidth, (int)(targetSize.Y - bottomHeight), (int)centerWidth, bottomHeight),
   136→            ninePatch.Bottom, Color.White);
   137→
   138→        // Bottom-right corner
   139→        spriteBatch.Draw(spriteInfo.SpriteSheet,
   140→            new Rectangle((int)(targetSize.X - rightWidth), (int)(targetSize.Y - bottomHeight), rightWidth, bottomHeight),
   141→            ninePatch.BottomRight, Color.White);
   142→
   143→        spriteBatch.End();
   144→
   145→        // Reset render target
   146→        graphicsDevice.SetRenderTarget(null);
   147→
   148→        // Cache the result
   149→        _ninePatchTextureCache[cacheKey] = renderTarget;
   150→        
   151→        return renderTarget;
   152→    }
   153→
   154→    public override void Dispose()
   155→    {
   156→        // Clean up cached textures
   157→        foreach (var texture in _ninePatchTextureCache.Values)
   158→        {
   159→            texture?.Dispose();
   160→        }
   161→        _ninePatchTextureCache.Clear();
   162→    }
   163→}

