   126→    [Subscribe]
   127→    private void OnCollision(in CollisionMessage message)
   128→    {
   129→        if (message.Type != CollisionType.Dialogue) return;
   130→        if (_dialogueState.IsActive || _dialogueState.WasTriggered) return;
   131→
   132→        ActivateDialogue("Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.");
   133→    }
   134→
   135→    public void ActivateDialogue(string text)
   136→    {
   137→        _dialogueState.IsActive = true;
   138→        _dialogueState.WasTriggered = true;
   139→
   140→        // Show box
   141→        _dialogueState.BoxEntity.Set<Visible>();
   142→
   143→        // Set text content and reset reveal
   144→        ref var dynamicText = ref _dialogueState.TextEntity.Get<DynamicText>();
   145→        dynamicText.TextContent = text;
   146→        dynamicText.VisibleCharacterCount = 0;
   147→        dynamicText.IsRevealed = false;
   148→        dynamicText.RevealStartTime = float.NaN;
   149→
   150→        // Hide indicator until text is fully revealed
   151→        if (_dialogueState.IndicatorEntity.Has<Visible>())
   152→            _dialogueState.IndicatorEntity.Remove<Visible>();
   153→
   154→        _world.Publish(new DialogueActiveMessage(true));
   155→    }
   156→
   157→    public void Update(GameState state)
   158→    {
   159→        if (!_dialogueState.IsActive) return;
   160→
   161→        ref var dynamicText = ref _dialogueState.TextEntity.Get<DynamicText>();
   162→
   163→        // Show/hide continue indicator based on text reveal
   164→        if (dynamicText.IsRevealed)
   165→        {
   166→            if (!_dialogueState.IndicatorEntity.Has<Visible>())
   167→                _dialogueState.IndicatorEntity.Set<Visible>();
   168→        }
   169→        else
   170→        {
   171→            if (_dialogueState.IndicatorEntity.Has<Visible>())
   172→                _dialogueState.IndicatorEntity.Remove<Visible>();
   173→        }
   174→
   175→        // Check for dismiss input
   176→        if (InputState.Interact.Pressed(state))
   177→        {
   178→            DeactivateDialogue();
   179→        }
   180→    }
   181→
   182→    private void DeactivateDialogue()
   183→    {
   184→        _dialogueState.IsActive = false;
   185→
   186→        // Hide box
   187→        if (_dialogueState.BoxEntity.Has<Visible>())
   188→            _dialogueState.BoxEntity.Remove<Visible>();
   189→
   190→        // Clear box draw component
   191→        var boxDraw = _dialogueState.BoxEntity.Get<DrawComponent>();
   192→        boxDraw.Texture = null;
   193→
   194→        // Clear text
   195→        ref var dynamicText = ref _dialogueState.TextEntity.Get<DynamicText>();
   196→        dynamicText.VisibleCharacterCount = 0;
   197→        dynamicText.IsRevealed = false;
   198→        dynamicText.TextContent = "";
   199→
   200→        var textDraw = _dialogueState.TextEntity.Get<DrawComponent>();
   201→        textDraw.Text = null;
   202→        textDraw.Font = null;
   203→
   204→        // Hide indicator
   205→        if (_dialogueState.IndicatorEntity.Has<Visible>())
   206→            _dialogueState.IndicatorEntity.Remove<Visible>();
   207→
   208→        var indicatorDraw = _dialogueState.IndicatorEntity.Get<DrawComponent>();
   209→        indicatorDraw.Texture = null;
   210→
   211→        _world.Publish(new DialogueActiveMessage(false));
   212→    }
