    11→using MonoDreams.Message;
    12→using MonoDreams.State;
    13→
    14→namespace MonoDreams.System.Level;
    15→
    16→/// <summary>
    17→/// Processes LDtk tile layers when a new level is loaded and creates individual entities for each tile.
    18→/// This system replaces the old TilemapRenderPrepSystem approach of bundling all tiles into single entities.
    19→/// </summary>
    20→public sealed class LDtkTileParserSystem : ISystem<GameState>
    21→{
    22→    private const float TILEMAP_BASE_LAYER_DEPTH = 0.09f;
    23→    private const float TILEMAP_LAYER_DEPTH_STEP = 0.001f;
    24→
    25→    private readonly World _world;
    26→    private readonly ContentManager _content;
    27→    private readonly IDisposable _levelLoadedSubscription;
    28→    private readonly IDisposable _levelUnloadedSubscription;
    29→    private readonly Dictionary<string, Texture2D> _loadedTextures = new();
    30→    
    31→    // Track tile entities for cleanup
    32→    private readonly HashSet<Entity> _tileEntities = new();
    33→
    34→    public bool IsEnabled { get; set; } = true;
    35→
    36→    public LDtkTileParserSystem(World world, ContentManager content)
    37→    {
    38→        _world = world ?? throw new ArgumentNullException(nameof(world));
    39→        _content = content ?? throw new ArgumentNullException(nameof(content));
    40→
    41→        _levelLoadedSubscription = _world.SubscribeWorldComponentAdded<CurrentLevelComponent>(HandleLevelLoaded);
    42→        _levelUnloadedSubscription = _world.SubscribeWorldComponentRemoved<CurrentLevelComponent>(HandleLevelUnloaded);
    43→
    44→        if (_world.Has<CurrentLevelComponent>())
    45→        {
    46→            HandleLevelLoaded(_world, _world.Get<CurrentLevelComponent>());
    47→        }
    48→    }
    49→
    50→    private void HandleLevelLoaded(World _, in CurrentLevelComponent currentLevelComp)
    51→    {
    52→        if (!IsEnabled) return;
    53→
    54→        Console.WriteLine($"Parsing tiles for level '{currentLevelComp.LevelData?.Identifier}'...");
    55→        
    56→        // Clean up existing tiles
    57→        CleanupTileEntities();
    58→        
    59→        var levelData = currentLevelComp.LevelData;
    60→        if (levelData?.LayerInstances == null)
    61→        {
    62→            Console.WriteLine("No layer instances found in level data.");
    63→            return;
    64→        }
    65→
    66→        var currentLayerDepth = TILEMAP_BASE_LAYER_DEPTH;
    67→        int tilesProcessed = 0;
    68→
    69→        foreach (var layer in levelData.LayerInstances)
    70→        {
    71→            // Process only Tile and Auto layers
    72→            if (layer._Type != LayerType.Tiles && layer._Type != LayerType.AutoLayer) continue;
    73→            if (!layer.Visible) continue;
    74→
    75→            if (string.IsNullOrEmpty(layer._TilesetRelPath))
    76→            {
    77→                Console.WriteLine($"Skipping layer {layer._Identifier} - no tileset path.");
    78→                continue;
    79→            }
    80→
    81→            // Load tileset texture
    82→            if (!_loadedTextures.TryGetValue(layer._TilesetRelPath, out var tilesetTexture))
    83→            {
    84→                try
    85→                {
    86→                    string assetPath = layer._TilesetRelPath.Replace(".png", "").Replace(".aseprite", "");
    87→                    tilesetTexture = _content.Load<Texture2D>(assetPath);
    88→                    _loadedTextures.Add(layer._TilesetRelPath, tilesetTexture);
    89→                }
    90→                catch (Exception ex)
    91→                {
    92→                    Console.WriteLine($"Failed to load tileset '{layer._TilesetRelPath}': {ex.Message}");
    93→                    continue;
    94→                }
    95→            }
    96→
    97→            // Process tiles in this layer
    98→            var tiles = layer._Type == LayerType.Tiles ? layer.GridTiles : layer.AutoLayerTiles;
    99→            
   100→            foreach (var tile in tiles)
   101→            {
   102→                var identifier = layer._Identifier == "Wall_AutoLayer" ? "Wall" : "Tile";
   103→                // Create tile spawn request
   104→                var tileRequest = new EntitySpawnRequest(
   105→                    identifier: identifier,
   106→                    instanceIid: $"tile_{layer.Iid}_{tile.Px.X}_{tile.Px.Y}",
   107→                    position: new Vector2(tile.Px.X, tile.Px.Y),
   108→                    size: new Vector2(layer._GridSize, layer._GridSize),
   109→                    pivot: Vector2.Zero,
   110→                    tilesetPosition: new Vector2(tile.Src.X, tile.Src.Y),
   111→                    layer: layer,
   112→                    customFields: new Dictionary<string, object>
   113→                    {
   114→                        ["layerDepth"] = currentLayerDepth,
   115→                        ["tilesetTexture"] = tilesetTexture,
   116→                        ["tileId"] = tile.T
   117→                    }
   118→                );
   119→
   120→                _world.Publish(tileRequest);
   121→                tilesProcessed++;
   122→            }
   123→
   124→            currentLayerDepth -= TILEMAP_LAYER_DEPTH_STEP;
   125→        }
   126→
   127→        Console.WriteLine($"Published {tilesProcessed} tile spawn requests for level '{levelData.Identifier}'.");
   128→    }
   129→
   130→    private void HandleLevelUnloaded(World _, in CurrentLevelComponent currentLevelComp)
   131→    {
   132→        if (!IsEnabled) return;
   133→        Console.WriteLine($"Cleaning up tiles for level '{currentLevelComp.LevelData?.Identifier}'...");
   134→        CleanupTileEntities();
   135→    }
   136→
   137→    private void CleanupTileEntities()
   138→    {
   139→        Console.WriteLine($"Disposing {_tileEntities.Count} tile entities...");
   140→        foreach (var entity in _tileEntities.ToArray())
   141→        {
   142→            if (entity.IsAlive)
   143→            {
   144→                entity.Dispose();
   145→            }
   146→        }
   147→        _tileEntities.Clear();
   148→    }
   149→
   150→    public void Update(GameState state)
   151→    {
   152→        // Processing happens on component addition/removal events
   153→    }
   154→
   155→    public void Dispose()
   156→    {
   157→        _levelLoadedSubscription?.Dispose();
   158→        _levelUnloadedSubscription?.Dispose();
   159→        CleanupTileEntities();
   160→        _loadedTextures.Clear();
   161→        GC.SuppressFinalize(this);
   162→    }
   163→}
