    14→/// <summary>
    15→/// Modular entity spawning system that delegates entity creation to registered factories.
    16→/// This allows for extensible entity creation without modifying the core system.
    17→/// </summary>
    18→public class EntitySpawnSystem : ISystem<GameState>
    19→{
    20→    public bool IsEnabled { get; set; } = true;
    21→    
    22→    private readonly World _world;
    23→    private readonly ContentManager _content;
    24→    private readonly IReadOnlyDictionary<RenderTargetID, RenderTarget2D> _renderTargets;
    25→    private readonly Dictionary<string, IEntityFactory> _entityFactories;
    26→
    27→    public EntitySpawnSystem(World world, ContentManager content, IReadOnlyDictionary<RenderTargetID, RenderTarget2D> renderTargets)
    28→    {
    29→        _world = world;
    30→        _content = content;
    31→        _renderTargets = renderTargets;
    32→        _entityFactories = new Dictionary<string, IEntityFactory>();
    33→        
    34→        _world.Subscribe<EntitySpawnRequest>(OnEntitySpawnRequest);
    35→    }
    36→
    37→    /// <summary>
    38→    /// Register a custom entity factory for a specific identifier
    39→    /// </summary>
    40→    public void RegisterEntityFactory(string identifier, IEntityFactory factory)
    41→    {
    42→        _entityFactories[identifier] = factory;
    43→    }
    44→
    45→    /// <summary>
    46→    /// Remove an entity factory
    47→    /// </summary>
    48→    public void UnregisterEntityFactory(string identifier)
    49→    {
    50→        _entityFactories.Remove(identifier);
    51→    }
    52→
    53→    [Subscribe]
    54→    private void OnEntitySpawnRequest(in EntitySpawnRequest request)
    55→    {
    56→        if (_entityFactories.TryGetValue(request.Identifier, out var factory))
    57→        {
    58→            factory.CreateEntity(_world, request);
    59→        }
    60→        else
    61→        {
    62→            // Log warning for unknown entity types
    63→            Console.WriteLine($"Warning: No factory registered for entity type '{request.Identifier}'");
    64→        }
    65→    }
    66→
    67→    public void Update(GameState state)
    68→    {
    69→        // This system is event-driven, no per-frame updates needed
    70→    }
    71→
    72→    public void Dispose()
    73→    {
    74→        GC.SuppressFinalize(this);
    75→    }
    76→}
