     7→/// <summary>
     8→/// Message published when an LDtk entity instance should be spawned into the ECS world.
     9→/// Contains parsed data from the LDtkEntityInstance.
    10→/// </summary>
    11→public readonly struct EntitySpawnRequest
    12→{
    13→    /// <summary>
    14→    /// The identifier of the entity definition in LDtk (e.g., "PlayerStart", "NPC").
    15→    /// </summary>
    16→    public readonly string Identifier;
    17→
    18→    /// <summary>
    19→    /// The unique instance identifier (IID) of the entity in the LDtk level.
    20→    /// </summary>
    21→    public readonly string InstanceIid;
    22→
    23→    /// <summary>
    24→    /// The world position (top-left) of the entity in pixels.
    25→    /// </summary>
    26→    public readonly Vector2 Position;
    27→
    28→    /// <summary>
    29→    /// The size of the entity in pixels.
    30→    /// </summary>
    31→    public readonly Vector2 Size;
    32→
