     9→using MonoDreams.State;
    10→
    11→namespace MonoDreams.System.Draw;
    12→
    13→// Prepares DrawElements for static sprites and 9-patches
    14→[With(typeof(DrawComponent), typeof(SpriteInfo), typeof(Transform), typeof(Visible))]
    15→public class SpritePrepSystem(World world, GraphicsDevice graphicsDevice, bool pixelPerfectRendering) : AEntitySetSystem<GameState>(world)
    16→{
    17→    private readonly bool _pixelPerfectRendering = pixelPerfectRendering;
    18→    private readonly Dictionary<string, Texture2D> _ninePatchTextureCache = new();
    19→
    20→    protected override void Update(GameState state, in Entity entity)
    21→    {
    22→        ref readonly var transform = ref entity.Get<Transform>();
    23→        ref readonly var spriteInfo = ref entity.Get<SpriteInfo>();
    24→        ref readonly var drawComponent = ref entity.Get<DrawComponent>();
    25→
    26→        if (spriteInfo.NinePatchData.HasValue && spriteInfo.SpriteSheet != null)
    27→        {
    28→            // Create a runtime nine-patch texture
    29→            var ninePatchTexture = CreateNinePatchTexture(spriteInfo);
    30→
    31→            var position = transform.WorldPosition + spriteInfo.Offset;
    32→            drawComponent.Texture = ninePatchTexture;
    33→            drawComponent.Position = _pixelPerfectRendering
    34→                ? new Vector2(MathF.Round(position.X), MathF.Round(position.Y))
    35→                : position;
    36→            drawComponent.Rotation = transform.WorldRotation;
    37→            drawComponent.Scale = transform.WorldScale;
    38→            drawComponent.Size = spriteInfo.Size;
    39→            drawComponent.SourceRectangle = null;
    40→            drawComponent.Color = spriteInfo.Color;
    41→            drawComponent.LayerDepth = spriteInfo.LayerDepth;
    42→            drawComponent.Origin = spriteInfo.Origin;
    43→        }
    44→        // --- Handle Regular Sprite ---
    45→        else if (spriteInfo.SpriteSheet != null)
    46→        {
    47→            var position = transform.WorldPosition + spriteInfo.Offset;
    48→            drawComponent.Texture = spriteInfo.SpriteSheet;
    49→            drawComponent.Position = _pixelPerfectRendering
    50→                ? new Vector2(MathF.Round(position.X), MathF.Round(position.Y))
    51→                : position;
    52→            drawComponent.Rotation = transform.WorldRotation;
    53→            drawComponent.Scale = transform.WorldScale;
    54→            drawComponent.Size = spriteInfo.Size;
    55→            drawComponent.SourceRectangle = spriteInfo.Source;
    56→            drawComponent.Color = spriteInfo.Color;
    57→            drawComponent.LayerDepth = spriteInfo.LayerDepth;
    58→            drawComponent.Origin = spriteInfo.Origin;
    59→        }
    60→    }
    61→
    62→    private Texture2D CreateNinePatchTexture(SpriteInfo spriteInfo)
    63→    {
    64→        var ninePatch = spriteInfo.NinePatchData.Value;
    65→        var targetSize = spriteInfo.Size;
    66→        
    67→        // Create a cache key based on source texture, nine-patch data, and target size
    68→        var cacheKey = $"{spriteInfo.SpriteSheet.GetHashCode()}_{ninePatch.GetHashCode()}_{targetSize.X}x{targetSize.Y}";
    69→        
    70→        if (_ninePatchTextureCache.TryGetValue(cacheKey, out var cachedTexture))
    71→        {
    72→            return cachedTexture;
    73→        }
    74→
    75→        // Create new render target with the target size
    76→        var renderTarget = new RenderTarget2D(graphicsDevice, 
    77→            (int)targetSize.X, (int)targetSize.Y, 
    78→            false, SurfaceFormat.Color, DepthFormat.None);
    79→
    80→        // Create a temporary SpriteBatch for rendering to the render target
    81→        using var spriteBatch = new SpriteBatch(graphicsDevice);
    82→        
    83→        // Set render target and clear
    84→        graphicsDevice.SetRenderTarget(renderTarget);
    85→        graphicsDevice.Clear(Color.Transparent);
    86→
    87→        spriteBatch.Begin(SpriteSortMode.Immediate, BlendState.AlphaBlend, 
    88→            SamplerState.PointClamp, DepthStencilState.None, RasterizerState.CullNone);
    89→
    90→        // Calculate dimensions for stretching
    91→        var leftWidth = ninePatch.TopLeft.Width;
    92→        var rightWidth = ninePatch.TopRight.Width;
    93→        var topHeight = ninePatch.TopLeft.Height;
    94→        var bottomHeight = ninePatch.BottomLeft.Height;
    95→        
    96→        var centerWidth = targetSize.X - leftWidth - rightWidth;
    97→        var centerHeight = targetSize.Y - topHeight - bottomHeight;
    98→
    99→        // Draw the 9 patches
   100→        // Top-left corner
   101→        spriteBatch.Draw(spriteInfo.SpriteSheet, 
   102→            new Rectangle(0, 0, leftWidth, topHeight),
   103→            ninePatch.TopLeft, Color.White);
   104→
   105→        // Top edge (stretched horizontally)
   106→        spriteBatch.Draw(spriteInfo.SpriteSheet,
   107→            new Rectangle(leftWidth, 0, (int)centerWidth, topHeight),
   108→            ninePatch.Top, Color.White);
   109→
   110→        // Top-right corner
   111→        spriteBatch.Draw(spriteInfo.SpriteSheet,
   112→            new Rectangle((int)(targetSize.X - rightWidth), 0, rightWidth, topHeight),
   113→            ninePatch.TopRight, Color.White);
   114→
   115→        // Left edge (stretched vertically)
   116→        spriteBatch.Draw(spriteInfo.SpriteSheet,
   117→            new Rectangle(0, topHeight, leftWidth, (int)centerHeight),
   118→            ninePatch.Left, Color.White);
   119→
   120→        // Center (stretched both ways)
   121→        spriteBatch.Draw(spriteInfo.SpriteSheet,
   122→            new Rectangle(leftWidth, topHeight, (int)centerWidth, (int)centerHeight),
   123→            ninePatch.Center, Color.White);
   124→
   125→        // Right edge (stretched vertically)
   126→        spriteBatch.Draw(spriteInfo.SpriteSheet,
   127→            new Rectangle((int)(targetSize.X - rightWidth), topHeight, rightWidth, (int)centerHeight),
   128→            ninePatch.Right, Color.White);
   129→
   130→        // Bottom-left corner
   131→        spriteBatch.Draw(spriteInfo.SpriteSheet,
   132→            new Rectangle(0, (int)(targetSize.Y - bottomHeight), leftWidth, bottomHeight),
   133→            ninePatch.BottomLeft, Color.White);
   134→
   135→        // Bottom edge (stretched horizontally)
   136→        spriteBatch.Draw(spriteInfo.SpriteSheet,
   137→            new Rectangle(leftWidth, (int)(targetSize.Y - bottomHeight), (int)centerWidth, bottomHeight),
   138→            ninePatch.Bottom, Color.White);
   139→
   140→        // Bottom-right corner
   141→        spriteBatch.Draw(spriteInfo.SpriteSheet,
   142→            new Rectangle((int)(targetSize.X - rightWidth), (int)(targetSize.Y - bottomHeight), rightWidth, bottomHeight),
   143→            ninePatch.BottomRight, Color.White);
   144→
   145→        spriteBatch.End();
   146→
   147→        // Reset render target
   148→        graphicsDevice.SetRenderTarget(null);
   149→
   150→        // Cache the result
   151→        _ninePatchTextureCache[cacheKey] = renderTarget;
   152→        
   153→        return renderTarget;
   154→    }
   155→
   156→    public override void Dispose()
   157→    {
   158→        // Clean up cached textures
   159→        foreach (var texture in _ninePatchTextureCache.Values)
   160→        {
   161→            texture?.Dispose();
   162→        }
   163→        _ninePatchTextureCache.Clear();
   164→    }
   165→}
