   105→    private SequentialSystem<GameState> CreateUpdateSystem()
   106→    {
   107→        var inputSystems = new ParallelSystem<GameState>(_parallelRunner,
   108→            new CursorInputSystem(_world),
   109→            new InputMappingSystem(_world)
   110→        );
   111→        
   112→        var blenderParser = new BlenderLevelParserSystem(_world, _content, _camera);
   113→        blenderParser.RegisterCollectionHandler("Player", entity => entity.Set(new PlayerState()));
   114→
   115→        var entitySpawnSystem = new EntitySpawnSystem(_world, _content, _renderTargets);
   116→        entitySpawnSystem.RegisterEntityFactory("Tile", new TileEntityFactory(_content));
   117→        entitySpawnSystem.RegisterEntityFactory("Wall", new WallEntityFactory(_content));
   118→        entitySpawnSystem.RegisterEntityFactory("Player", new PlayerEntityFactory(_content));
   119→        entitySpawnSystem.RegisterEntityFactory("Enemy", new NPCEntityFactory(_content));
   120→
   121→        var levelLoadSystems = new SequentialSystem<GameState>(
   122→            new LevelLoadRequestSystem(_world, _content),
   123→            blenderParser,
   124→            new LDtkTileParserSystem(_world, _content),
   125→            new LDtkEntityParserSystem(_world),
   126→            entitySpawnSystem);
   127→        
   128→        // Collision pipeline must run sequentially (movement → velocity → detect → resolve → commit)
   129→        // Individual systems keep their internal _parallelRunner for entity-level parallelism
   130→        var logicSystems = new SequentialSystem<GameState>(
   131→            new MovementSystem(_world, _parallelRunner),
   132→            new OrbSystem(_world),
   133→            new TransformVelocitySystem(_world, _parallelRunner),
   134→            new TransformCollisionDetectionSystem<CollisionMessage>(_world, _parallelRunner, CollisionMessage.Create),
   135→            new TransformPhysicalCollisionResolutionSystem(_world),
   136→            new TransformCommitSystem(_world, _parallelRunner),
   137→            new TextUpdateSystem(_world), // Logic only
   138→            new DialogueSystem(_world, _content, _graphicsDevice, _viewportManager.VirtualWidth, _viewportManager.VirtualHeight)
   139→            // ... other game logic systems
   140→        );
   141→        
   142→        // Transform hierarchy system must run AFTER logic systems modify transforms
   143→        // but BEFORE any systems read world transforms (camera, rendering, etc.)
   144→        var transformHierarchySystem = new TransformHierarchySystem(_world);
   145→
   146→        var cameraFollowSystem = new CameraFollowSystem(_world, _camera);
   147→
   148→        // Cursor position must update AFTER camera has moved to avoid 1-frame lag
   149→        var cursorLateUpdateSystem = new CursorPositionSystem(_world, _camera, _viewportManager);
   150→
   151→        var debugSystems = new ParallelSystem<GameState>(_parallelRunner,
   152→            new ColliderDebugSystem(_world, _graphicsDevice)
   153→        );
   154→
   155→        return new SequentialSystem<GameState>(
   156→            // new DebugSystem(_world, _game, _spriteBatch), // If needed
   157→            inputSystems,
   158→            levelLoadSystems,
   159→            logicSystems,
   160→            transformHierarchySystem, // Propagate transform hierarchy dirty flags
   161→            cameraFollowSystem,
   162→            cursorLateUpdateSystem,          // Cursor position updates after camera
   163→            new CursorDrawPrepSystem(_world) // Draw prep after position is finalized
   164→            // debugSystems
   165→        );
   166→    }
   167→    
   168→    private SequentialSystem<GameState> CreateDrawSystem()
   169→    {
   170→        var pixelPerfectRendering = SettingsManager.Instance.Settings.PixelPerfectRendering;
   171→
   172→        // Systems that prepare DrawComponent based on state (can often be parallel)
   173→        var prepDrawSystems = new SequentialSystem<GameState>( // Or parallel if clearing is handled carefully
   174→            // Optional: A system to clear all DrawComponents first?
   175→            // new ClearDrawComponentSystem(_world),
   176→            new CullingSystem(_world, _camera),
   177→            new SpritePrepSystem(_world, _graphicsDevice, pixelPerfectRendering),
   178→            new TextPrepSystem(_world, pixelPerfectRendering),
   179→            new MeshPrepSystem(_world)
   180→            // new SpriteDebugSystem(_world)  // Debug visualization for sprite bounds and origins
   181→            // ... other systems preparing DrawElements (UI, particles, etc.)
   182→        );
   183→
   184→        // The single system that handles all rendering (strictly sequential)
   185→        var renderSystem = new MasterRenderSystem(
   186→            _spriteBatch,
   187→            _graphicsDevice,
   188→            _camera,
   189→            _renderTargets, // Pass the dictionary/collection of RTs
   190→            _world
   191→        );
   192→    
   193→        // Final system to draw RenderTargets to backbuffer (if needed)
   194→        var finalDrawToScreenSystem = new FinalDrawSystem(_spriteBatch, _graphicsDevice, _viewportManager, _camera, _renderTargets);
   195→        
   196→        return new SequentialSystem<GameState>(
   197→            prepDrawSystems,
   198→            renderSystem,
   199→            finalDrawToScreenSystem // Draw RTs to screen
   200→            // new DrawDebugSystem(_world, _spriteBatch, _renderer) // If needed
   201→        );
   202→    }
   203→
   204→    public void Dispose()
   205→    {
   206→        UpdateSystem.Dispose();
   207→        GC.SuppressFinalize(this);
   208→    }
   209→}

