    14→/// <summary>
    15→/// Unified rendering system that handles all draw types: sprites, text, nine-patch, and mesh.
    16→/// This system is game-agnostic - it only renders what's in DrawComponent without any
    17→/// specialized component handling.
    18→/// </summary>
    19→public class MasterRenderSystem(
    20→    SpriteBatch spriteBatch,
    21→    GraphicsDevice graphicsDevice,
    22→    MonoDreams.Component.Camera camera,
    23→    IReadOnlyDictionary<RenderTargetID, RenderTarget2D> renderTargets,
    24→    World world) : ISystem<GameState>
    25→{
    26→    private BasicEffect? _basicEffect;
    27→
    28→    private enum BatchType { None, Sprite, Mesh }
    29→
    30→    private static BatchType GetBatchType(DrawElementType type) =>
    31→        type == DrawElementType.Mesh ? BatchType.Mesh : BatchType.Sprite;
    32→
    33→    private void EnsureBasicEffect()
    34→    {
    35→        _basicEffect ??= new BasicEffect(graphicsDevice)
    36→        {
    37→            VertexColorEnabled = true,
    38→            View = Matrix.Identity,
    39→            Projection = Matrix.CreateOrthographicOffCenter(0, camera.VirtualWidth, camera.VirtualHeight, 0, 0, 1)
    40→        };
    41→    }
    42→
    43→    private void BeginSpriteBatch(Matrix transformMatrix)
    44→    {
    45→        spriteBatch.Begin(
    46→            sortMode: SpriteSortMode.Deferred, // We pre-sort, so no need for FrontToBack
    47→            blendState: BlendState.AlphaBlend,
    48→            samplerState: SamplerState.LinearClamp,
    49→            depthStencilState: DepthStencilState.None,
    50→            rasterizerState: RasterizerState.CullNone,
    51→            effect: null,
    52→            transformMatrix: transformMatrix);
    53→    }
    54→
    55→    private void EndSpriteBatch() => spriteBatch.End();
    56→
    57→    private void ResetGraphicsStateForMeshRendering()
    58→    {
    59→        // Reset states after SpriteBatch.End() before mesh rendering
    60→        graphicsDevice.BlendState = BlendState.AlphaBlend;
    61→        graphicsDevice.DepthStencilState = DepthStencilState.None;
    62→        graphicsDevice.RasterizerState = RasterizerState.CullNone;
    63→        graphicsDevice.SamplerStates[0] = SamplerState.PointClamp;
    64→    }
    65→
    66→    public void Update(GameState state)
    67→    {
    68→        EnsureBasicEffect();
    69→
    70→        foreach (var renderTarget in renderTargets)
    71→        {
    72→            var queryBuilder = world.GetEntities()
    73→                .With((in DrawComponent e) => e.Target == renderTarget.Key);
    74→
    75→            // Main target: respect CullingSystem visibility. HUD/UI: always render.
    76→            if (renderTarget.Key == RenderTargetID.Main)
    77→                queryBuilder.With<Visible>();
    78→
    79→            var drawList = queryBuilder.AsSet();
    80→
    81→            graphicsDevice.SetRenderTarget(renderTarget.Value);
    82→            graphicsDevice.Clear(Color.Transparent);
    83→
    84→            var transformMatrix = Matrix.Identity;
    85→            if (renderTarget.Key == RenderTargetID.Main)
    86→            {
    87→                transformMatrix = camera.GetViewTransformationMatrix();
    88→            }
    89→
    90→            // Sort ALL entities by LayerDepth (stable sort preserves order for same depth)
    91→            var entities = drawList.GetEntities().ToArray();
    92→            var sortedEntities = entities
    93→                .Select((entity, index) => (entity, index, dc: entity.Get<DrawComponent>()))
    94→                .Where(x => x.dc.Type != DrawElementType.Mesh || x.dc.HasValidMesh)
    95→                .OrderBy(x => x.dc.LayerDepth)
    96→                .ThenBy(x => x.index) // Stable sort
    97→                .ToList();
    98→
    99→            if (sortedEntities.Count > 0)
   100→            {
   101→                RenderInterleaved(sortedEntities, transformMatrix);
   102→            }
   103→        }
   104→    }
   105→
   106→    private void RenderInterleaved(
   107→        List<(Entity entity, int index, DrawComponent dc)> sortedEntities,
   108→        Matrix transformMatrix)
   109→    {
   110→        BatchType currentBatch = BatchType.None;
   111→
   112→        foreach (var (_, _, dc) in sortedEntities)
   113→        {
   114→            var requiredBatch = GetBatchType(dc.Type);
   115→
   116→            // Batch type changed - switch context
   117→            if (requiredBatch != currentBatch)
   118→            {
   119→                // End previous batch
   120→                if (currentBatch == BatchType.Sprite)
   121→                    EndSpriteBatch();
   122→
   123→                // Start new batch
   124→                if (requiredBatch == BatchType.Sprite)
   125→                    BeginSpriteBatch(transformMatrix);
   126→                else if (requiredBatch == BatchType.Mesh)
   127→                    ResetGraphicsStateForMeshRendering();
   128→
   129→                currentBatch = requiredBatch;
   130→            }
   131→
   132→            // Draw the element
   133→            if (requiredBatch == BatchType.Sprite)
   134→                DrawElement(dc);
   135→            else
   136→                DrawSingleMesh(dc, transformMatrix);
   137→        }
   138→
   139→        // End final batch if sprites
   140→        if (currentBatch == BatchType.Sprite)
   141→            EndSpriteBatch();
   142→    }
   143→
   144→    private void DrawSingleMesh(DrawComponent dc, Matrix transformMatrix)
   145→    {
   146→        if (!dc.HasValidMesh) return;
   147→
   148→        _basicEffect!.Projection = Matrix.CreateOrthographicOffCenter(
   149→            0, camera.VirtualWidth, camera.VirtualHeight, 0, 0, 1);
   150→        _basicEffect.World = (dc.WorldMatrix ?? Matrix.Identity) * transformMatrix;
   151→
   152→        foreach (var pass in _basicEffect.CurrentTechnique.Passes)
   153→        {
   154→            pass.Apply();
   155→            graphicsDevice.DrawUserIndexedPrimitives(
   156→                dc.PrimitiveType,
   157→                dc.Vertices,
   158→                0,
   159→                dc.Vertices!.Length,
   160→                dc.Indices,
   161→                0,
   162→                dc.GetPrimitiveCount());
   163→        }
   164→    }
