    73→            // The Content.RootDirectory gives us the path to the Content folder
    74→            var jsonPath = Path.Combine(AppDomain.CurrentDomain.BaseDirectory, _content.RootDirectory, "blender_level.json");
    75→            Console.WriteLine($"Loading Blender level from: {jsonPath}");
    76→            var jsonContent = File.ReadAllText(jsonPath);
