    83→            var levelData = JsonSerializer.Deserialize<BlenderLevelData>(jsonContent, options);
    84→
    85→            if (levelData?.Objects == null)
    86→            {
    87→                Console.WriteLine("No objects found in Blender level data.");
    88→                return;
    89→            }
    90→
    91→            Console.WriteLine($"Loaded {levelData.Objects.Count} objects from Blender level.");
