    93→            foreach (var obj in levelData.Objects)
    94→            {
    95→                switch (obj.Type)
    96→                {
    97→                    case "CAMERA":
    98→                        ProcessCamera(obj, levelData.ScaleFactor);
    99→                        break;
   100→                    case "MESH":
   101→                    case "GREASEPENCIL":
