   124→    private void ProcessCamera(BlenderObject cameraObj, float scaleFactor)
   125→    {
   126→        if (cameraObj.Position == null) return;
   127→
   128→        var cameraPosition = new Vector2(cameraObj.Position.X, cameraObj.Position.Y);
   129→        _camera.Position = cameraPosition;
   130→
   131→        // Read ortho_scale from custom properties and convert to game zoom
   132→        // Blender ortho_scale = visible width in Blender units
   133→        // With scaleFactor, visible world pixels = ortho_scale * scaleFactor
   134→        // Game zoom = virtualWidth / visible_world_pixels
   135→        if (cameraObj.CustomProperties?.TryGetValue("zoom", out var zoomValue) == true)
   136→        {
   137→            if (zoomValue is JsonElement jsonElement && jsonElement.TryGetDouble(out var orthoScale))
   138→            {
   139→                // Convert Blender ortho_scale to game zoom
   140→                var visibleWorldWidth = orthoScale * scaleFactor;
   141→                var gameZoom = _camera.VirtualWidth / visibleWorldWidth;
   142→                _camera.Zoom = (float)gameZoom;
   143→                Console.WriteLine($"Set camera zoom to {gameZoom} (from Blender ortho_scale {orthoScale}, scaleFactor {scaleFactor})");
   144→            }
   145→        }
   146→
   147→        Console.WriteLine($"Set camera position to ({cameraPosition.X}, {cameraPosition.Y})");
   148→    }
