   150→    private void ProcessMesh(BlenderObject meshObj)
   151→    {
   152→        if (meshObj.UvMapping == null)
   153→        {
   154→            Console.WriteLine($"Skipping mesh '{meshObj.Name}' - no UV mapping.");
   155→            return;
   156→        }
   157→
   158→        // Extract content path from texture path
   159→        var contentPath = ExtractContentPath(meshObj.UvMapping.TexturePath);
   160→        if (string.IsNullOrEmpty(contentPath))
   161→        {
   162→            Console.WriteLine($"Skipping mesh '{meshObj.Name}' - invalid texture path: {meshObj.UvMapping.TexturePath}");
   163→            return;
   164→        }
   165→
   166→        // Load texture
   167→        Texture2D texture;
   168→        if (!_loadedTextures.TryGetValue(contentPath, out texture))
   169→        {
   170→            try
   171→            {
   172→                texture = _content.Load<Texture2D>(contentPath);
   173→                _loadedTextures[contentPath] = texture;
   174→                Console.WriteLine($"Loaded texture: {contentPath}");
   175→            }
   176→            catch (Exception ex)
   177→            {
   178→                Console.WriteLine($"Failed to load texture '{contentPath}': {ex.Message}");
   179→                return;
   180→            }
   181→        }
   182→
   183→        // Get UV coordinates from first layer
   184→        var uvLayer = meshObj.UvMapping.UvLayers?.FirstOrDefault();
   185→        if (uvLayer?.UvCoordinates == null || uvLayer.UvCoordinates.Count == 0)
   186→        {
   187→            Console.WriteLine($"Skipping mesh '{meshObj.Name}' - no UV coordinates.");
   188→            return;
   189→        }
   190→
   191→        // Calculate source rectangle from UV coordinates
   192→        var sourceRect = CalculateSourceRect(uvLayer.UvCoordinates, texture);
   193→
   194→        // Create entity
   195→        var entity = _world.CreateEntity();
   196→        _blenderEntities.Add(entity);
   197→
   198→        // Position from Blender
   199→        var position = new Vector2(
   200→            meshObj.Position?.X ?? 0,
   201→            meshObj.Position?.Y ?? 0
   202→        );
   203→
   204→        // Transform
   205→        entity.Set(new Transform(position));
   206→
   207→        // SpriteInfo with calculated source rectangle
   208→        // Use mesh dimensions from Blender for Size (already scaled), fallback to source rect if not available
   209→        var size = new Vector2(
   210→            meshObj.Dimensions?.X ?? sourceRect.Width,
   211→            meshObj.Dimensions?.Y ?? sourceRect.Height
   212→        );
   213→
   214→        // Calculate origin from Blender's origin offset
   215→        // Blender origin offset is normalized (0-1) where (0,0) is bottom-left and (0.5,0.5) is center
   216→        // SpriteBatch.Draw expects origin in SOURCE TEXTURE coordinates (not destination size)
   217→        var originOffsetX = meshObj.OriginOffset?.X ?? 0.5f;  // Default to center
   218→        var originOffsetY = meshObj.OriginOffset?.Y ?? 0.5f;
   219→        // Y is inverted: Blender Y=0 is bottom, engine Y=0 is top
   220→        // Use sourceRect dimensions since SpriteBatch.Draw origin is in source coordinates
   221→        var origin = new Vector2(sourceRect.Width * originOffsetX, sourceRect.Height * (1 - originOffsetY));
   222→
   223→        entity.Set(new SpriteInfo
   224→        {
   225→            SpriteSheet = texture,
   226→            Source = sourceRect,
   227→            Size = size,
   228→            Color = Color.White,
   229→            Target = RenderTargetID.Main,
   230→            LayerDepth = DEFAULT_LAYER_DEPTH,
   231→            Origin = origin
   232→        });
   233→
   234→        // DrawComponent
   235→        entity.Set(new DrawComponent
   236→        {
   237→            Type = DrawElementType.Sprite,
   238→            Target = RenderTargetID.Main
   239→        });
   240→
   241→        // Make visible
   242→        entity.Set(new Visible());
   243→
   244→        // Process collection-based components
   245→        ProcessCollections(entity, meshObj, size);
   246→
   247→        Console.WriteLine($"Created entity for mesh '{meshObj.Name}' at ({position.X}, {position.Y}) with size ({size.X}, {size.Y}), origin ({origin.X}, {origin.Y}), and source rect ({sourceRect.X}, {sourceRect.Y}, {sourceRect.Width}, {sourceRect.Height})");
   248→    }
