   250→    /// <summary>
   251→    /// Extracts the MonoGame Content path from a full file system path.
   252→    /// Finds "/Content/" and returns the substring after it, without file extension.
   253→    /// </summary>
   254→    private static string ExtractContentPath(string fullPath)
   255→    {
   256→        if (string.IsNullOrEmpty(fullPath))
   257→            return null;
   258→
   259→        var contentIndex = fullPath.IndexOf("/Content/", StringComparison.OrdinalIgnoreCase);
   260→        if (contentIndex < 0)
   261→        {
   262→            // Try Windows-style path
   263→            contentIndex = fullPath.IndexOf("\\Content\\", StringComparison.OrdinalIgnoreCase);
   264→        }
   265→
   266→        if (contentIndex < 0)
   267→            return null;
   268→
   269→        var separatorLength = "/Content/".Length;
   270→        var relativePath = fullPath.Substring(contentIndex + separatorLength);
   271→
   272→        // Remove file extension
   273→        var extensionIndex = relativePath.LastIndexOf('.');
   274→        if (extensionIndex > 0)
   275→        {
   276→            relativePath = relativePath.Substring(0, extensionIndex);
   277→        }
   278→
   279→        return relativePath;
   280→    }
