   282→    /// <summary>
   283→    /// Calculates a source rectangle from UV coordinates.
   284→    /// Assumes a plane mesh with 4 vertices.
   285→    /// </summary>
   286→    private static Rectangle CalculateSourceRect(List<UvCoordinate> uvCoords, Texture2D texture)
   287→    {
   288→        if (uvCoords == null || uvCoords.Count == 0)
   289→            return Rectangle.Empty;
   290→
   291→        float minU = uvCoords.Min(uv => uv.U);
   292→        float maxU = uvCoords.Max(uv => uv.U);
   293→        float minV = uvCoords.Min(uv => uv.V);
   294→        float maxV = uvCoords.Max(uv => uv.V);
   295→
   296→        // Convert UV to pixel coordinates with rounding for better precision
   297→        // UV origin is bottom-left, texture origin is top-left, so flip V
   298→        int x = (int)Math.Round(minU * texture.Width);
   299→        int y = (int)Math.Round((1 - maxV) * texture.Height);
   300→        int width = (int)Math.Round((maxU - minU) * texture.Width);
   301→        int height = (int)Math.Round((maxV - minV) * texture.Height);
   302→
   303→        return new Rectangle(x, y, width, height);
   304→    }
