   306→    /// <summary>
   307→    /// Processes Blender collection membership and adds appropriate components.
   308→    /// Collection names determine entity behavior (e.g., "Collision" adds BoxCollider).
   309→    /// Collection custom properties can configure component settings.
   310→    /// </summary>
   311→    private void ProcessCollections(Entity entity, BlenderObject meshObj, Vector2 size)
   312→    {
   313→        if (meshObj.Collections == null || meshObj.Collections.Count == 0)
   314→            return;
   315→
   316→        // Compute visual offset from origin so collision boxes align with rendered sprites.
   317→        // SpriteBatch.Draw renders at: position - origin * scale
   318→        // In destination space: offset = -(size * originOffset)
   319→        var originOffsetX = meshObj.OriginOffset?.X ?? 0.5f;
   320→        var originOffsetY = meshObj.OriginOffset?.Y ?? 0.5f;
   321→        // Compute float edges of visual bounds relative to entity position
   322→        float left = -size.X * originOffsetX;
   323→        float top = -size.Y * (1 - originOffsetY);  // Y inverted: Blender Y=0 is bottom
   324→        // Snap to smallest enclosing integer rectangle (floor expands left/top, ceiling expands right/bottom)
   325→        int intLeft = (int)Math.Floor(left);
   326→        int intTop = (int)Math.Floor(top);
   327→        int intRight = (int)Math.Ceiling(left + size.X);
   328→        int intBottom = (int)Math.Ceiling(top + size.Y);
   329→
   330→        var boundsOffset = new Point(intLeft, intTop);
   331→        var boundsSize = new Point(intRight - intLeft, intBottom - intTop);
   332→
   333→        // Check for Collision collection
   334→        if (meshObj.Collections.Contains("Collision"))
   335→        {
   336→            entity.Set(new EntityInfo("Collision"));
   337→                
   338→            // Get collision layer from collection properties (default to -1 for all layers)
   339→            int layer = -1;
   340→            bool passive = false;
   341→
   342→            if (meshObj.CollectionProperties?.TryGetValue("Collision", out var collisionProps) == true)
   343→            {
   344→                layer = GetIntProperty(collisionProps, "layer", -1);
   345→                passive = GetBoolProperty(collisionProps, "passive", false);
   346→            }
   347→
   348→            var bounds = new Rectangle(boundsOffset, boundsSize);
   349→            var activeLayers = new HashSet<int> { layer };
   350→            entity.Set(new BoxCollider(bounds, activeLayers, passive));
   351→
   352→            Console.WriteLine($"Added BoxCollider to '{meshObj.Name}' (layer: {layer}, passive: {passive})");
   353→        }
   354→
   355→        // Check for Player collection
   356→        if (meshObj.Collections.Contains("Player"))
   357→        {
   358→            entity.Set(new EntityInfo("Player"));
   359→            entity.Set(new BoxCollider(new Rectangle(boundsOffset, boundsSize)));
   360→            entity.Set(new RigidBody());
   361→            entity.Set(new Velocity());
   362→
   363→            // Add camera follow target component
   364→            entity.Set(new CameraFollowTarget
   365→            {
   366→                DampingX = 5.0f,
   367→                DampingY = 5.0f,
   368→                MaxDistanceX = 150.0f,
   369→                MaxDistanceY = 100.0f,
   370→                IsActive = true
   371→            });
   372→            Console.WriteLine($"Set Player for '{meshObj.Name}'");
   373→        }
   374→
   375→        // Check for Enemy collection
   376→        if (meshObj.Collections.Contains("Enemy"))
   377→        {
   378→            entity.Set(new EntityInfo("Enemy"));
   379→            Console.WriteLine($"Set Enemy for '{meshObj.Name}'");
   380→        }
   381→
   382→        // Check for Trigger collection (collision but no physics push)
   383→        if (meshObj.Collections.Contains("Trigger"))
   384→        {
   385→            if (!entity.Has<BoxCollider>())
   386→            {
   387→                var bounds = new Rectangle(boundsOffset, boundsSize);
   388→                entity.Set(new BoxCollider(bounds, passive: true));
   389→            }
   390→            entity.Set(new EntityInfo("Trigger"));
   391→            Console.WriteLine($"Set as Trigger zone for '{meshObj.Name}'");
   392→        }
   393→
   394→        // Invoke registered game-specific collection handlers
   395→        foreach (var collection in meshObj.Collections)
   396→        {
   397→            if (_collectionHandlers.TryGetValue(collection, out var handler))
   398→            {
   399→                handler(entity);
   400→            }
   401→        }
   402→    }
