   404→    /// <summary>
   405→    /// Gets an integer property from collection properties dictionary.
   406→    /// </summary>
   407→    private static int GetIntProperty(Dictionary<string, object> props, string key, int defaultValue)
   408→    {
   409→        if (props == null || !props.TryGetValue(key, out var value))
   410→            return defaultValue;
   411→
   412→        if (value is JsonElement jsonElement)
   413→        {
   414→            if (jsonElement.TryGetInt32(out var intVal))
   415→                return intVal;
   416→            if (jsonElement.TryGetDouble(out var doubleVal))
   417→                return (int)doubleVal;
   418→        }
   419→
   420→        if (value is int intValue)
   421→            return intValue;
   422→        if (value is double doubleValue)
   423→            return (int)doubleValue;
   424→        if (value is float floatValue)
   425→            return (int)floatValue;
   426→
   427→        return defaultValue;
   428→    }
   429→
   430→    /// <summary>
   431→    /// Gets a boolean property from collection properties dictionary.
   432→    /// </summary>
   433→    private static bool GetBoolProperty(Dictionary<string, object> props, string key, bool defaultValue)
   434→    {
   435→        if (props == null || !props.TryGetValue(key, out var value))
   436→            return defaultValue;
   437→
   438→        if (value is JsonElement jsonElement)
   439→        {
   440→            if (jsonElement.ValueKind == JsonValueKind.True)
   441→                return true;
   442→            if (jsonElement.ValueKind == JsonValueKind.False)
   443→                return false;
   444→        }
   445→
   446→        if (value is bool boolValue)
   447→            return boolValue;
   448→
   449→        return defaultValue;
   450→    }
