     7→public class Camera
     8→{
     9→    private float _zoom;
    10→    private float _rotation;
    11→    private Vector2 _position;
    12→    private Matrix _transform = Matrix.Identity;
    13→    private bool _isViewTransformationDirty = true;
    14→    private Matrix _camTranslationMatrix = Matrix.Identity;
    15→    private Matrix _camRotationMatrix = Matrix.Identity;
    16→    private Matrix _camScaleMatrix = Matrix.Identity;
    17→    private Matrix _resTranslationMatrix = Matrix.Identity;
    18→
    19→    private Vector3 _camTranslationVector = Vector3.Zero;
    20→    private Vector3 _camScaleVector = Vector3.Zero;
    21→    private Vector3 _resTranslationVector = Vector3.Zero;
    22→
    23→    private (float zoom, Vector2 cached) _viewSize = (float.NaN, Vector2.Zero);
    24→    private (Vector2 position, Vector2 viewSize, Rectangle cached) _virtualScreenBounds;
    25→    
    26→    // Camera operates in virtual resolution space
    27→    private readonly int _virtualWidth;
    28→    private readonly int _virtualHeight;
    29→
    30→    public int VirtualWidth => _virtualWidth;
    31→    public int VirtualHeight => _virtualHeight;
    32→
    33→    public Camera(int virtualWidth = 800, int virtualHeight = 600) // Use configured virtual size
    34→    {
    35→        _virtualWidth = virtualWidth;
    36→        _virtualHeight = virtualHeight;
    37→        _zoom = 1.0f;
    38→        _rotation = 0.0f;
    39→        _position = Vector2.Zero;
    40→    }
    41→
    42→    public Vector2 ViewSize
    43→    {
    44→        get {
    45→            if (_viewSize.zoom != _zoom)
    46→            {
    47→                _viewSize = (_zoom, new Vector2(_virtualWidth / _zoom, _virtualHeight / _zoom));
    48→            }
    49→            return _viewSize.cached;
    50→        }
    51→    }
    52→
    53→    public Vector2 Position
    54→    {
    55→        get => _position;
    56→        set
    57→        {
    58→            _position = value;
    59→            _isViewTransformationDirty = true;
    60→        }
    61→    }
    62→
    63→    public float Zoom
    64→    {
    65→        get => _zoom;
    66→        set
    67→        {
    68→            _zoom = value;
    69→            if (_zoom < 0.1f)
    70→            {
    71→                _zoom = 0.1f;
    72→            }
    73→            _isViewTransformationDirty = true;
    74→        }
    75→    }
    76→
    77→    public float Rotation
    78→    {
    79→        get => _rotation;
    80→        set
    81→        {
    82→            _rotation = value;
    83→            _isViewTransformationDirty = true;
    84→        }
    85→    }
    86→
    87→    public Rectangle VirtualScreenBounds
    88→    {
    89→        get
    90→        {
    91→            if (_virtualScreenBounds.position != Position || _virtualScreenBounds.viewSize != ViewSize)
    92→            {
    93→                var bounds = new Rectangle( new Point((int)(Position.X - ViewSize.X * 0.5f), (int)(Position.Y - ViewSize.Y * 0.5f)), ViewSize.ToPoint());
    94→                _virtualScreenBounds = (Position, ViewSize, bounds);
    95→            }
    96→            return _virtualScreenBounds.cached;
    97→        }
    98→    }
    99→
   100→    public Matrix GetViewTransformationMatrix()
   101→    {
   102→        if (!_isViewTransformationDirty) return _transform;
   103→        
   104→        _camTranslationVector.X = -_position.X;
   105→        _camTranslationVector.Y = -_position.Y;
   106→
   107→        Matrix.CreateTranslation(ref _camTranslationVector, out _camTranslationMatrix);
   108→        Matrix.CreateRotationZ(_rotation, out _camRotationMatrix);
   109→
   110→        _camScaleVector.X = _zoom;
   111→        _camScaleVector.Y = _zoom;
   112→        _camScaleVector.Z = 1;
   113→
   114→        Matrix.CreateScale(ref _camScaleVector, out _camScaleMatrix);
   115→
   116→        // Translate origin to center of the virtual screen for rotation/zoom
   117→        _resTranslationVector.X = _virtualWidth * 0.5f;
   118→        _resTranslationVector.Y = _virtualHeight * 0.5f;
   119→        _resTranslationVector.Z = 0;
   120→
   121→        Matrix.CreateTranslation(ref _resTranslationVector, out _resTranslationMatrix);
   122→
   123→        _transform = _camTranslationMatrix 
   124→                     * _camRotationMatrix
   125→                     * _camScaleMatrix
   126→                     * _resTranslationMatrix;
   127→
   128→        _isViewTransformationDirty = false;
   129→
   130→        return _transform;
   131→    }
   132→
   133→    public void RecalculateTransformationMatrices()
   134→    {
   135→        _isViewTransformationDirty = true;
   136→    }
   137→
   138→    /// <summary>
   139→    /// Converts virtual screen coordinates (e.g., mouse position scaled to virtual resolution)
   140→    /// to world coordinates.
   141→    /// </summary>
   142→    /// <param name="virtualScreenPosition">Coordinates in the virtual screen space (0,0 to VirtualWidth, VirtualHeight).</param>
   143→    /// <returns>World coordinates.</returns>
   144→    public Vector2 VirtualScreenToWorld(Vector2 virtualScreenPosition)
   145→    {
   146→        Matrix invViewMatrix = Matrix.Invert(GetViewTransformationMatrix());
   147→        return Vector2.Transform(virtualScreenPosition, invViewMatrix);
   148→    }
   149→}

